/// <summary>
///     This method enable / disable the prenote response days on selection of <c>isBankPrenote</c> field value.
/// </summary>
/// <returns>
///     1 if enabled; otherwise, 0.
/// </returns>
public int active()
{
    int ret;

    ret = super();

    element.enableButtons();
    element.updateDesign();
    element.enableActiveDates();

    if (bankAccountTable.IsBankPrenote == NoYes::No)
    {
        prenotes_PrenoteResponseDays.enabled(false);
    }
    else
    {
        prenotes_PrenoteResponseDays.enabled(true);
    }

    return ret;
}

/// <summary>
/// Edits the bank reconciliation enabling parameter.
/// </summary>
/// <param name="set">
/// whether the paramter is changed.
/// </param>
/// <param name="_bankAccountTable">
/// The bank account table buffer.
/// </param>
/// <param name="_bankReconciliationEnabled">
/// Whether the bank reconciliation is set enabled.
/// </param>
/// <returns>
/// Whether the bank reconciliation is enabled.
/// </returns>
public edit BankReconciliationEnabled editBankReconciliationEnabled(boolean set, BankAccountTable _bankAccountTable, BankReconciliationEnabled _bankReconciliationEnabled)
{
    if (set)
    {
        if (_bankReconciliationEnabled)
        {
            if (Box::okCancel(
                    "@CashManagement:AdvancedBankReconciliationEnableAlert",
                    DialogButton::Cancel,
                    "@GLS221674") == DialogButton::Ok)
            {
                if (_bankAccountTable.checkCanEnableAdvancedReconciliation())
                {
                    _bankAccountTable.BankReconciliationEnabled = _bankReconciliationEnabled;
                }
            }
        }
        else
        {
            if (_bankAccountTable.checkCanDisableAdvancedReconciliation())
            {
                _bankAccountTable.BankReconciliationEnabled = _bankReconciliationEnabled;
            }
        }
    }

    return _bankAccountTable.isBankReconciliationEnabled();
}

