public boolean validate()
{
    boolean isValid;

    isValid = super();

    if (isValid)
    {
        BankPositivePayFormat bankPositivePayFormatSelect;

        select bankPositivePayFormatSelect
            where bankPositivePayFormatSelect.FormatName == bankAccountTable.BankPositivePayFormatName
                && bankPositivePayFormatSelect.IsPayroll == isPayrollAccount;

        if (!bankPositivePayFormatSelect.RecId)
        {
            bankAccountTable.BankPositivePayFormatName = '';
            bankAccountTable.BankPositivePayStartDate = dateNull();
        }
    }

    return isValid;
}

