public void clicked()
{
    BankGroup bankGroup = BankGroup::find(BankAccountTable.BankGroupId);
    super();

    Bank_JP::validateBankNameKana(
        BankAccountTable.AccountNameKana_JP,
        tableNum(BankAccountTable),
        fieldNum(BankAccountTable, AccountNameKana_JP));

    if (bankGroup)
    {
        Bank_JP::validateBankNameKana(
            bankGroup.NameKana_JP,
            tableNum(BankGroup),
            fieldnum(BankGroup, NameKana_JP));
        Bank_JP::validateBankNameKana(
            bankGroup.BranchNameKana_JP,
            tableNum(BankGroup),
            fieldnum(BankGroup, BranchNameKana_JP));
    }
    else
    {
        warning("@CashManagement:BankGroupsMustBeSetForBankAccounts_JP");
    }
}

