BankAccountStatementNum accountStatement()
{
    return bankAccountTrans.AccountStatement;
}

void bookRefresh()
{
    bankAccountTrans_ds.research();
}

void buttonsActive()
{
    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        cashDueAnnouncement_RU.enabled(
            (select firstonly RecId from ledgerJournalTrans
                where ledgerJournalTrans.Voucher == bankAccountTrans.Voucher &&
                      ledgerJournalTrans.AnnouncementNum_RU).RecId);
    }
    // </GEERU>

    if (isInquiryForm)
    {
        voucherTransactions.enabled(true);
        cheques.enabled(false);
        deposit.enabled(false);
        originalDocument.enabled(true);
        bankPaym2Invoice.enabled(true);
        printout.enabled(false);

        if (bankAccountTrans.DepositNum)
        {
            voucherTransactions.enabled(false);
        }
    }
    else
    {
        originalDocument.enabled(true);
        cheques.enabled(false);
        deposit.enabled(false);
        voucherTransactions.enabled(true);
        bankPaym2Invoice.enabled(true);
        printDepositSlip.enabled(false);

        if (bankAccountTrans.DepositNum)
        {
            voucherTransactions.enabled(false);
            deposit.enabled(true);
            originalDocument.enabled(true);
            bankPaym2Invoice.enabled(false);
            printDepositSlip.enabled(true);
        }

        if (bankAccountTrans.ChequeNum)
        {
            cheques.enabled(true);
            originalDocument.enabled(true);
        }
    }

    if (bankAccountTrans.LedgerTransType == LedgerTransType::ExchAdjustment)
    {
        BankPaym2Invoice.enabled(false);
        MenuItemButton.enabled(false);
    }

}

private void setSummarizationControlStatus()
{
    if (bankAccountTrans.SummarizationId)
    {
        PaymentSummarizationDetails.enabled(true);
        VoucherTransactions.enabled(false);
        BankPaym2Invoice.enabled(false);
    }
    else
    {
        PaymentSummarizationDetails.enabled(false);
    }
}

BankDepositNum depositNum()
{
    return bankAccountTrans.DepositNum;
}

public void init()
{
    isInquiryForm = false;

    if (element.args().record())
    {
        if (element.args().record().TableId == tableNum(BankAccountTrans)
            && (element.args().caller().name() == formStr(LedgerJournalTransBankChequeReversal)
                || element.args().caller().name() == formStr(LedgerJournalTransBankDepositPaymCancel)
                || element.args().caller().name() == formStr(BankDocumentTableListPage)))
        {
            isInquiryForm = true;
        }
    }

    super();

    reconciliationFilter.selection(enum2int(element.args().parmEnum()));

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoRU ]))
    {
        bankAccountTrans_ds.object(fieldNum(BankAccountTrans, Included)).visible(false);
        bankAccountTrans_ds.object(fieldNum(BankAccountTrans, AccountStatement)).allowEdit(false);
        correction.allowEdit(false);
    }
    // </GEERU>

    // <GEELV>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoLV ]))
    {
        this.initDesign_LV();
    }
    // </GEELV>

    overviewGrid.setFocus();

    if (FeatureStateProvider::isFeatureEnabled(BankBridgingVendorTransactionDetailsFeature::instance()))
    {
        OverviewGrid_ClearedDate.visible(true);
    }

    element.setSummarizationControlStatus();
}

void initDesign_LV()
{
    bankAccountTrans_DS.allowEdit(true);

    //Disable editability of all fields for datasource
    allowEditFieldsOnFormDS_W(bankAccountTrans_DS, false);

    //Enable editability of Notification fields.
    bankAccountTrans_ds.object(fieldNum(BankAccountTrans, CorrespondentCountry_LV)).allowEdit(true);
    bankAccountTrans_ds.object(fieldNum(BankAccountTrans, CentralBankPurposeText_LV)).allowEdit(true);
    bankAccountTrans_ds.object(fieldNum(BankAccountTrans, CentralBankPurposeCode_LV)).allowEdit(true);
    bankAccountTrans_ds.object(fieldNum(BankAccountTrans, BankPaymentRegistrationNum_LV)).allowEdit(true);
}

void setExchAdjustment_PL(PlExcludeExchAdj _excludeExchAdjustment_PL)
{
    if (DialogButton::Ok == Box::okCancel(strFmt("@GLS113937", fieldPName(BankAccountTransPL, ExcludeExchAdj_PL)), DialogButton::Ok))
    {
        bankAccountTransPL.ExcludeExchAdj_PL = _excludeExchAdjustment_PL;
        bankAccountTransPL_DS.research();
    }
}

int table()
{
    return tableNum(BankAccountTrans);
}

void init()
{
    super();

    criteriaOpen = this.query().dataSourceNo(1).addRange(fieldNum(BankAccountTrans, Reconciled));

    if (isInquiryForm)
    {
        this.query().dataSourceNo(1).clearDynalinks();
        this.query().dataSourceNo(1).addRange(fieldNum(BankAccountTrans, RecId)).value(SysQuery::value(element.args().record().RecId));
    }

    this.queryBuildDataSource().addSortField(fieldNum(BankAccountTrans, TransDate), SortOrder::Descending);
}

void executeQuery()
{
    str                                 callingform;
    BankAccountTransReconilationType    bankAccountTransReconilationType;

    if (element.args().record())
    {
        callingform =  element.args().caller().name();
        if (callingform == formStr(BankAccountTable))
        {
            bankAccountTransReconilationType = reconciliationFilter.selection();
            switch (bankAccountTransReconilationType)
            {
                case BankAccountTransReconilationType::Transactions:
                    criteriaOpen.value('0..1');
                    break;
                case BankAccountTransReconilationType::ReconciledTransactions:
                    criteriaOpen.value('1');
                    break;
                case BankAccountTransReconilationType::UnreconciledTransactions:
                    criteriaOpen.value('0');
                    break;
            }
        }
    }

    super();
}

int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.buttonsActive();
        element.setSummarizationControlStatus();
    }

    return ret;
}

public boolean validateWrite()
{
    // <GEELV>
    // We do not want to execute the ReasonReferenceTable validateWrite to ensure the reason and reason comment are not blank.
    // BankAccountTrans table are posted transactions, but LV needs to be able edit the correspondent country information to ensure
    // legal country reporting.  The BankAccountTrans form does not allow editing of the ReasonReferenceTable fields, so we are
    // overriding the validateWrite functionality.
    boolean ret = true;

    return ret;
    // </GEELV>
}

public boolean validateWrite()
{
    #IsoCountryRegionCodes
    boolean ret = true;

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#IsoHU, #IsoPL]))
    {
        if (!bankAccountTransPL.BankAccountTrans)
        {
            bankAccountTrans_DS.write();
            bankAccountTransPL.BankAccountTrans = bankAccountTrans.RecId;
        }

        ret = super();
    }

    return ret;
}

/// <summary>
/// Retrieves data from joined data sources
/// when a user navigates to a new record and then sets the new record as the current record.
/// </summary>
/// <returns>Always returns 1 </returns>
public int active()
{
    int ret;

    ret = super();
    if (ret)
    {
        exchangeAdjustment_PL.enabled(bankAccountTrans.LedgerTransType != LedgerTransType::ExchAdjustment);
        Include.visible(bankAccountTransPL.ExcludeExchAdj_PL);
        Exclude.visible(!bankAccountTransPL.ExcludeExchAdj_PL);
    }
    return ret;
}

