public int task(int _taskId)
{
    #Task

    switch (_taskId)
    {
        case #taskRefresh:
            formView.performFormRefresh();
            break;

        default:
            break;
    }

    return super(_taskId);
}

private void updateStatementFilterSummary(str _summary = '')
{
    if (!_summary)
    {
        StatementLineFilterSummaryDisplayButton.visible(false);
    }
    else
    {
        StatementLineFilterSummaryDisplayButton.text(_summary);
        StatementLineFilterSummaryDisplayButton.visible(true);
    }
}

/// <summary>
/// Applies the filters for open statement.
/// </summary>
/// <param name = "_fromDate">The from date.</param>
/// <param name = "_toDate">The to date.</param>
/// <param name = "_debitAmountFrom">The debit amount from.</param>
/// <param name = "_debitAmountTo">The debit amount to.</param>
/// <param name = "_creditAmountFrom">The credit amount from.</param>
/// <param name = "_creditAmountTo">The credit amount to.</param>
/// <param name = "_transCodes">The transaction codes.</param>
/// <param name = "_tradingParty">The trading party.</param>
/// <param name = "_referenceNumber">The reference number.</param>
/// <param name = "_identification">The identification.</param>
/// <param name = "_summary">The summary.</param>
internal void applyStatementOpenFilters(
    date _fromDate,
    date _toDate,
    real _debitAmountFrom,
    real _debitAmountTo,
    real _creditAmountFrom,
    real _creditAmountTo,
    container _transCodes,
    str _tradingParty,
    str _referenceNumber,
    str _identification,
    str _summary)
{
    formView.queryAddRangeDate(BankReconciliationStatementOpen_ds, _fromDate, _toDate);
    formView.queryAddRangeAmount(BankReconciliationStatementOpen_ds, _debitAmountFrom, _debitAmountTo, _creditAmountFrom, _creditAmountTo);
    formView.queryAddRangeTrans(BankReconciliationStatementOpen_ds, _transCodes);
    formView.queryAddRangeParty(BankReconciliationStatementOpen_ds, _tradingParty);
    formView.queryAddRangeReference(BankReconciliationStatementOpen_ds, _referenceNumber);
    formView.queryAddRangeIdentification(BankReconciliationStatementOpen_ds, _identification);
    BankReconciliationStatementOpen_ds.executeQuery();

    this.updateStatementFilterSummary(_summary);
}

private void updateDocumentFilterSummary(str _summary = '')
{
    if (!_summary)
    {
        DocumentFilterSummaryDisplayButton.visible(false);
    }
    else
    {
        DocumentFilterSummaryDisplayButton.text(_summary);
        DocumentFilterSummaryDisplayButton.visible(true);
    }
}

/// <summary>
/// Applies the filters for open document.
/// </summary>
/// <param name = "_fromDate">The from date.</param>
/// <param name = "_toDate">The to date.</param>
/// <param name = "_debitAmountFrom">The debit amount from.</param>
/// <param name = "_debitAmountTo">The debit amount to.</param>
/// <param name = "_creditAmountFrom">The credit amount from.</param>
/// <param name = "_creditAmountTo">The credit amount to.</param>
/// <param name = "_transType">The transaction type.</param>
/// <param name = "_documentType">The document type.</param>
/// <param name = "_partyName">The party name.</param>
/// <param name = "_paymentReference">The payment reference.</param>
/// <param name = "_summary">The summary.</param>
internal void applyDocumentOpenFilters(
    date _fromDate,
    date _toDate,
    real _debitAmountFrom,
    real _debitAmountTo,
    real _creditAmountFrom,
    real _creditAmountTo,
    container _transType,
    BankDocumentBookType _documentType,
    str _partyName,
    str _paymentReference,
    str _summary)
{
    formView.queryAddRangeDate(BankReconciliationDocumentOpen_ds, _fromDate, _toDate);
    formView.queryAddRangeAmount(BankReconciliationDocumentOpen_ds, _debitAmountFrom, _debitAmountTo, _creditAmountFrom, _creditAmountTo);
    formView.queryAddRangeTrans(BankReconciliationDocumentOpen_ds, _transType);
    formView.queryAddRangeParty(BankReconciliationDocumentOpen_ds, _partyName);
    formView.queryAddRangeReference(BankReconciliationDocumentOpen_ds, _paymentReference);
    formView.queryAddRangeDocumentType(BankReconciliationDocumentOpen_ds, _documentType);
    BankReconciliationDocumentOpen_ds.executeQuery();

    this.updateDocumentFilterSummary(_summary);
}

