/// <summary>
/// Marks the bank document line.
/// </summary>
/// <param name = "_set">true if it to set, otherwise false.</param>
/// <param name = "_bankReconciliationDocumentOpenTmp">The bank document buffer.</param>
/// <param name = "_markTrans">Yes if it to mark, otherwise No.</param>
/// <returns>Yes if it is marked, otherwise No.</returns>
internal edit NoYes markTrans(boolean _set, BankReconciliationDocumentOpenTmp _bankReconciliationDocumentOpenTmp, NoYes _markTrans)
{
    if (_set)
    {
        formView.markDocument(_markTrans, _bankReconciliationDocumentOpenTmp.MatchingId, _bankReconciliationDocumentOpenTmp.RecId);
    }

    return formView.isMarked(_bankReconciliationDocumentOpenTmp.MatchingId);
}

