public void clicked()
{
    super();
    Args args = new Args();
    BankStmtISOReportEntry bankStmtISOReportEntrySelect = formView.getCurrentMatchedStatementLine();
    BankStatementLinePaymentInformation paymentInfo = BankStatementLinePaymentInformation::findByReportEntry(bankStmtISOReportEntrySelect.RecId);
    args.record(LedgerJournalTable::find(paymentInfo.JournalNum));
    if (bankStmtISOReportEntrySelect.BankPaymentType == BankPaymentType::CustPayment)
    {
        new MenuFunction(menuItemDisplayStr(LedgerJournalTableCustPaymCustTable), MenuItemType::Display).run(args);
    }
    else if (bankStmtISOReportEntrySelect.BankPaymentType == BankPaymentType::VendPayment)
    {
        new MenuFunction(menuItemDisplayStr(LedgerJournalTable5), MenuItemType::Display).run(args);
    }
}

public void clicked()
{
    super();

    BankStmtISOReportEntry bankStmtISOReportEntrySelect = formView.getCurrentMatchedStatementLine();

    Args args = new Args();
    args.name(formStr(LedgerTransVoucher));
    args.record(bankStmtISOReportEntrySelect);
    args.caller(this);

    FormRun formRun = ClassFactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.detach();
}

public void clicked()
{
    super();

    BankStmtISOReportEntry bankStmtISOReportEntrySelect = formView.getCurrentMatchedStatementLine();
    SourceDocumentHeaderImplementation sourceDocumentHeaderImplementation;
    BankStatementLinePostTable linePostTableLoc = bankStmtISOReportEntrySelect.getStatementLinePostTable();
    if (linePostTableLoc.RecId)
    {
        sourceDocumentHeaderImplementation = SourceDocumentHeader::find(linePostTableLoc.SourceDocumentHeader).getSourceDocumentImplementation();
    }
    else
    {
        BankStmtISOAccountStatement statement = bankStmtISOReportEntrySelect.getStatement();
        sourceDocumentHeaderImplementation = SourceDocumentHeader::find(statement.SourceDocumentHeader).getSourceDocumentImplementation();
    }
    Args args = new Args();
    args.caller(element);
    args.record(sourceDocumentHeaderImplementation);
    new MenuFunction(menuitemDisplayStr(SubledgerJournalAccountEntries), MenuItemType::Display).run(args);
}

public void clicked()
{
    super();

    BankStmtISOReportEntry bankStmtISOReportEntrySelect = formView.getCurrentMatchedStatementLine();
    BankStatementLinePostTable linePostTable = BankStatementLinePostTable::find(bankStmtISOReportEntrySelect.BankStatementLinePostTable);
    BankStatementLinePostTaxDocument bankStatementLinePostTaxDocument = BankStatementLinePostTaxDocument::construct(linePostTable);
    bankStatementLinePostTaxDocument.calcTax(TaxUncommitted::existByDocumentLineId(bankStmtISOReportEntrySelect.TableId, bankStmtISOReportEntrySelect.RecId));
    bankStatementLinePostTaxDocument.tax().sourceSingleLine(true, true);
    Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkBankStatement), bankStatementLinePostTaxDocument.tax(), bankStmtISOReportEntrySelect);
}

