private void openWorksheet(BankAutomationStatementFormViewWorksheetCaller _worksheetCaller, BankReconciliationHeader _reconciliationHeader)
{
    if (_worksheetCaller.inUseBlockReconciliation(false, _reconciliationHeader, NoYes::No))
    {
        Global::warning("@Bank:WorksheetIsBeingProcessingWarning");
    }
    else
    {
        if (BankModernReconciliationUpgradeWizardTrigger::checkExecutionStatusAndPromptBoxToTriggerWizard())
        {
            _worksheetCaller.initJournalTableData(_reconciliationHeader);
            Args args = new Args();
            MenuFunction worksheetMenuItem = new MenuFunction(menuitemDisplayStr(BankAutomationReconciliationWorksheet), MenuItemType::Display);
            args.caller(element);
            args.record(_reconciliationHeader);
            args.lookupRecord(_reconciliationHeader);
            worksheetMenuItem.run(args);
        }
    }
}

