internal display Voucher dispVoucher(BankStmtISOReportEntry _statementLine)
{
    if (!_statementLine.isPosted() && _statementLine.IsNewBankDocument)
    {
        FormDataSource formStatementDS = formView.parmDSStatement();
        BankStmtISOAccountStatement statement = formStatementDS.cursor();
        return statement.Voucher;
    }
    return _statementLine.Voucher;
}

internal display BankAutomationStatementLineMatchingStatusType dispStatementLineMatchingStatus(BankStmtISOReportEntry _statementLine)
{
    if (_statementLine.Reconciled)
    {
        return BankAutomationStatementLineMatchingStatusType::Reconciled;
    }

    BankReconciliationMatchingItem matchingItem = BankReconciliationMatchingItem::findValidByStatementLineRecId(_statementLine.RecId);
    if (matchingItem.RecId && matchingItem.BankReconciliationMatching)
    {
        BankReconciliationMatching matching = BankReconciliationMatching::find(matchingItem.BankReconciliationMatching);
        if (matching.RecId && !matching.Cancelled)
        {
            return BankAutomationStatementLineMatchingStatusType::Matched;
        }
    }

    return BankAutomationStatementLineMatchingStatusType::Unmatched;
}

