internal display str dispStatementLineNunmberIncludedAndReconciled(BankReconciliationHeader _record)
{
    str lineNumberStr;
    int lineNumber;
    BankStmtISOAccountStatement statement = BankStmtISOAccountStatement_DS.cursor();

    if (_record.IsReconciled)
    {
        lineNumber = BankAutomationStatementFormViewRelatedWorksheetPresenter::getReconciliedLineNumberInReconciliationHeader(_record, statement);
        lineNumberStr = any2Str(strFmt("@Bank:RelatedWorksheetReconciledLineFmt", lineNumber, lineNumber > 1 ? "@Bank:Lines" : "@Bank:Line"));
    }
    else
    {
        lineNumber = BankAutomationStatementFormViewRelatedWorksheetPresenter::getIncludedLineNumberInOpenReconciliationHeader(statement);
        lineNumberStr = any2Str(strFmt("@Bank:RelatedWorksheetIncludedLineFmt", lineNumber, lineNumber > 1 ? "@Bank:Lines" : "@Bank:Line"));
    }
    return lineNumberStr;
}

public void clicked()
{
    BankStmtISOAccountStatement statement = BankStmtISOAccountStatement_DS.cursor();
    BankReconciliationHeader openReconciliationHeader = BankReconciliationHeader::findActiveHeaderByAccountId(statement.BankAccountTable);

    TransDateTime cutoffDateTime = openReconciliationHeader.getCutOffDateTime();
    boolean isFromDateTimeCoveredByOpenWorksheet = cutoffDateTime >= statement.FromDateTime;

    if (isFromDateTimeCoveredByOpenWorksheet)
    {
        BankAutomationStatementFormViewWorksheetCaller worksheetCaller = formView.getWorksheetCaller();
        element.openWorksheet(worksheetCaller, openReconciliationHeader);
    }
    else
    {
        Args args = new Args();
        MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(BankReconciliationHeader), MenuItemType::Display);
        args.caller(element);
        BankAccountTable bankAccount = BankAccountTable::find(statement.BankAccountTable);
        args.record(bankAccount);
        menuFunction.run(args);
    }
}

public void clicked()
{
    super();
    Args args = new Args();
    BankStatementLinePaymentInformation paymentInfo = BankStatementLinePaymentInformation::findByReportEntry(BankStmtISOReportEntry.RecId);
    args.record(LedgerJournalTable::find(paymentInfo.JournalNum));
    if (BankStmtISOReportEntry.BankPaymentType == BankPaymentType::CustPayment)
    {
        new MenuFunction(menuItemDisplayStr(LedgerJournalTableCustPaymCustTable), MenuItemType::Display).run(args);
    }
    else if (BankStmtISOReportEntry.BankPaymentType == BankPaymentType::VendPayment)
    {
        new MenuFunction(menuItemDisplayStr(LedgerJournalTable5), MenuItemType::Display).run(args);
    }
}

public void clicked()
{
    BankStmtISOReportEntry statementLine = BankStmtISOReportEntry_DS.cursor();
    SourceDocumentHeaderImplementation sourceDocumentHeaderImplementation;
    BankStatementLinePostTable linePostTableLoc = statementLine.getStatementLinePostTable();
    if (linePostTableLoc.RecId)
    {
        sourceDocumentHeaderImplementation = SourceDocumentHeader::find(linePostTableLoc.SourceDocumentHeader).getSourceDocumentImplementation();
    }
    else
    {
        BankStmtISOAccountStatement statement = statementLine.getStatement();
        sourceDocumentHeaderImplementation = SourceDocumentHeader::find(statement.SourceDocumentHeader).getSourceDocumentImplementation();
    }
    Args args = new Args();
    args.caller(element);
    args.record(sourceDocumentHeaderImplementation);
    new MenuFunction(menuitemDisplayStr(SubledgerJournalAccountEntries), MenuItemType::Display).run(args);
}

public void clicked()
{
    BankStmtISOReportEntry statementLine = BankStmtISOReportEntry_ds.cursor();
    BankStatementLinePostTable linePostTable = BankStatementLinePostTable::find(statementLine.BankStatementLinePostTable);
    BankStatementLinePostTaxDocument bankStatementLinePostTaxDocument = BankStatementLinePostTaxDocument::construct(linePostTable);
    bankStatementLinePostTaxDocument.calcTax(TaxUncommitted::existByDocumentLineId(bankStmtISOReportEntry.TableId, bankStmtISOReportEntry.RecId));
    bankStatementLinePostTaxDocument.tax().sourceSingleLine(true, true);
    Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkBankStatement), bankStatementLinePostTaxDocument.tax(), bankStmtISOReportEntry);
}

