[Form]
public class BankAutomationStatementGenerateVoucherDialog extends FormRun
{
    private FormRun callerForm;
    private List bankStmtISOReportEntryRecIdList;

    private boolean isInitedDraftPostingData;
    private boolean isBulkEditing;
    private Map linePostTableRecIdToLineRecIdMap;
    private BankStatementLinePostInformationTmp tmpInfoTable;
    private BankStatementLinePostInformationTmp tmpInfoTableBulkEdit;

    boolean isFinTagConfigurationSetForCurrent = FinTagConfiguration::isFinTagConfigurationSet(curExt());

}

public void init()
{
    super();
    callerForm = this.args().caller();
    bankStmtISOReportEntryRecIdList = element.args().parmObject();

    if (isFinTagConfigurationSetForCurrent)
    {
        FinTagReferenceGroupController::registerReferenceGroup(
            FinTagReferenceGroupControllerContract::construct(FinTag));
        FinTagReferenceGroupController::registerReferenceGroup(
            FinTagReferenceGroupControllerContract::construct(FinTagBulkEdit));
    }
    else
    {
        FinTag.visible(false);
        FinTagBulkEdit.visible(false);
    }

    ListEnumerator enumerator = bankStmtISOReportEntryRecIdList.getEnumerator();

    // Initializes the tmp info record to each selected statement line.
    while (enumerator.moveNext())
    {
        RecId selectedStatementLineRecId = enumerator.current();
        QueryBuildRange range = BankStmtISOReportEntry_ds.queryBuildDataSource().addRange(fieldNum(BankStmtISOReportEntry, RecId));
        range.value(queryValue(selectedStatementLineRecId));

        BankStmtISOReportEntry statementLine = BankStmtISOReportEntry::find(selectedStatementLineRecId);
        BankStmtISOAccountStatement statement = statementLine.getStatement();

        tmpInfoTable.clear();
        tmpInfoTable.BankStmtISOReportEntry = selectedStatementLineRecId;
        tmpInfoTable.AccountingDate = statementLine.getBookingDate();
        tmpInfoTable.AccountingDistributionTemplate = statement.AccountingDistributionTemplate;
        tmpInfoTable.DefaultDimension = statement.DefaultDimension;
        tmpInfoTable.TransactionTxt = statementLine.EntryReference;
        tmpInfoTable.insert();
    }
    BankStatementLinePostInformationTmp.setTmpData(tmpInfoTable);

    // Initializes the BankStatementLinePostInformationTmpBulkEdit_DS an empty single unique tmp info record for bulk editing option.
    tmpInfoTableBulkEdit.insert();
    BankStatementLinePostInformationTmpBulkEdit.setTmpData(tmpInfoTableBulkEdit);

    OffsetLedgerDimension.parmValidateBlockedForManualEntry(true);
    OffsetLedgerDimensionBulkEdit.parmValidateBlockedForManualEntry(true);
}

public void closeOk()
{
    try
    {
        ttsbegin;
        this.initDraftPostingData();
        this.generateVoucherForLinePostTable();
        super();
        ttscommit;
    }
    catch
    {
        ttsabort;
        throw;
    }
    finally
    {
        isInitedDraftPostingData = false;
        element.refreshForm();
    }
}

private void refreshForm()
{
    if (callerForm && callerForm.name() == formStr(BankAutomationReconciliationWorksheet))
    {
        BankAutomationReconciliationWorksheetFormView worksheetFormView = callerForm.getFormView();
        worksheetFormView.refreshHeader();
        worksheetFormView.performFormRefresh();
    }
}

private void generateVoucherForLinePostTable()
{
    MapEnumerator enumerator = linePostTableRecIdToLineRecIdMap.getEnumerator();
    while (enumerator.moveNext())
    {
        RecId linePostTableRecId = enumerator.currentKey();
        RecId lineRecId = enumerator.currentValue();
        BankStatementLinePostTable linePostTable = BankStatementLinePostTable::find(linePostTableRecId);
        BankStmtISOReportEntry statementLine = BankStmtISOReportEntry::find(lineRecId);

        BankStatementLinePostInformationTmp localInfoTmpBuffer;
        localInfoTmpBuffer.setTmpData(tmpInfoTable);
        select firstonly localInfoTmpBuffer where localInfoTmpBuffer.BankStmtISOReportEntry == lineRecId;
        if (localInfoTmpBuffer.RecId && linePostTable.isReadyToPost())
        {
            statementLine.updateOrCreateLinePostingInfoByTmp(localInfoTmpBuffer);
        }
        else
        {
            throw Error(Error::wrongUseOfFunction(funcName()));
        }
    }

    BankStatementLineListPost::postLineList(bankStmtISOReportEntryRecIdList);
    FormDataSource lineDataSoruce = callerForm.dataSource(formDataSourceStr(BankAutomationStatementForm,BankStmtISOReportEntry));
    lineDataSoruce.research(true);
}

/// <summary>
/// Resets and clears the dialog generated data before closing by cancel or directly close window, or refresh.
/// </summary>
public void close()
{
    this.clearDraftPostingDataAndTmpData();
    super();
}

private void initDraftPostingData()
{
    if (!isInitedDraftPostingData)
    {
        isInitedDraftPostingData = true;
        linePostTableRecIdToLineRecIdMap = BankStatementLinePostTable::createLinePostTableMapForDraftPosting(bankStmtISOReportEntryRecIdList);
    }
}

private void clearDraftPostingDataAndTmpData()
{
    delete_from tmpInfoTable;
    delete_from tmpInfoTableBulkEdit;

    if (linePostTableRecIdToLineRecIdMap && !linePostTableRecIdToLineRecIdMap.empty())
    {
        MapEnumerator enumerator = linePostTableRecIdToLineRecIdMap.getEnumerator();

        while (enumerator.moveNext())
        {
            RecId linePostTableRecId = enumerator.currentKey();
            BankStatementLinePostTable linePostTableLoc = BankStatementLinePostTable::find(linePostTableRecId);
            if (linePostTableLoc.RecId && linePostTableLoc.PostingStatus != BankStatementLinePostStatus::Posted)
            {
                linePostTableLoc.deleteLinePostTableAndResetLine();
            }
        }
    }
    isInitedDraftPostingData = false;
}

private void updateBulkEditDesign(boolean _isBulkEditing, boolean _isMultiMarked = true)
{
    StatementLineGrid.enabled(!_isBulkEditing || !_isMultiMarked);
    StatementLineActionButtonGroup.visible(!_isMultiMarked);
    StatementLineBulkEditActionButtonGroup.visible(_isMultiMarked);
    FinancialTabPage.enabled(!_isMultiMarked);
    FinancialDimensionTabPage.enabled(!_isMultiMarked);
    FinancialTagTabPage.enabled(!_isMultiMarked);
    OKButton.enabled(!_isBulkEditing);

    BulkEditButton.visible(!_isBulkEditing);
    BulkEditCompleteButton.visible(_isBulkEditing);
    BulkEditCancelButton.visible(_isBulkEditing);
    FinancialTabPage.visible(!_isBulkEditing);
    FinancialDimensionTabPage.visible(!_isBulkEditing);
    FinancialTagTabPage.visible(!_isBulkEditing);
    FinancialTabPageBulkEdit.visible(_isBulkEditing);
    FinancialDimensionTabPageBulkEdit.visible(_isBulkEditing);
    FinancialTagTabPageBulkEdit.visible(_isBulkEditing);
}

private void copyBulkEditTmpDataToMarkedLineTmpData()
{
    BankStatementLinePostInformationTmp bulkEditInfoTmp = BankStatementLinePostInformationTmpBulkEdit_DS.cursor();
    Array markedLinesIndexArray = BankStmtISOReportEntry_DS.recordsMarked();
    BankStatementLinePostInformationTmp localTmpBuffer;
    localTmpBuffer.setTmpData(tmpInfoTable);

    for(int i = 1; i <= markedLinesIndexArray.lastIndex(); i++)
    {
        int lineIndex = markedLinesIndexArray.value(i);
        BankStmtISOReportEntry markedLine = BankStmtISOReportEntry_DS.cursor(lineIndex);
        select firstonly localTmpBuffer where localTmpBuffer.BankStmtISOReportEntry == markedLine.RecId;
        localTmpBuffer.copyAllPropertyFieldsOf(bulkEditInfoTmp);
    }
}

public void markChanged()
{
    super();
    boolean isMultiMarked = BankStmtISOReportEntry_DS.recordsMarked().lastIndex() > 1;
    element.updateBulkEditDesign(false, isMultiMarked);
}

