public void clicked()
{
    BankStatementLinePostInformationTmp tmpInformation = BankStatementLinePostInformationTmp_DS.cursor();
    element.initDraftPostingData();
    BankStmtISOReportEntry currentLine = BankStmtISOReportEntry_DS.cursor();
    currentLine.reread();
    currentLine.updateOrCreateLinePostingInfoByTmp(tmpInformation);

    BankStatementLinePostTable linePostTableLoc = currentLine.getStatementLinePostTable();
    BankStatementLinePostTaxDocument bankStatementLinePostTaxDocument = BankStatementLinePostTaxDocument::construct(linePostTableLoc);
    bankStatementLinePostTaxDocument.calcTax(TaxUncommitted::existByDocumentLineId(currentLine.TableId, currentLine.RecId));
    super();
}

