public void init()
{
    super();

    if (this.args() && this.args().record().TableId == tableNum(BankAccountTable))
    {
        BankAccount.visible(NoYes::No);
    }

    if (FeatureStateProvider::isFeatureEnabled(BankBridgingVendorTransactionDetailsFeature::instance()))
    {
        VendAccountNum.visible(true);
        VendName.visible(true);
        VendRemittanceLocation.visible(true);
        VendRemittanceAddress.visible(true);
    }
}

public void jumpRef()
{
    LedgerJournalTable ledgerJournalTable = LedgerJournalTable::find(ledgerEntryJournal.JournalNumber);
    str menuItemDisplayName = LedgerJournalStatic::menuItemNameForJournalType(ledgerJournalTable);

    Args jumpArgs = new Args(element);
    jumpArgs.record(ledgerJournalTable);

    MenuFunction menuFunction = new MenuFunction(menuItemDisplayName, MenuItemType::Display);
    menuFunction.run(jumpArgs);
}

public void jumpRef()
{
    Args jumpArgs = new Args(element);
    jumpArgs.record(generalJournalEntry);

    MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(LedgerTransVoucher), MenuItemType::Display);
    menuFunction.run(jumpArgs);
}

public void jumpRef()
{
    MenuFunction menuFunction;
    ModuleCustVend moduleCustVend;
    Args jumpArgs = new Args(element);
    str menuItemStr;

    switch (LedgerTransFurtherPosting.AccountType)
    {
        case LedgerJournalACType::Cust:
            moduleCustVend = moduleCustVend::Cust;
            menuItemStr = menuItemDisplayStr(CustPaymMode);
            break;

        case LedgerJournalACType::Vend:
            moduleCustVend = moduleCustVend::Vend;
            menuItemStr = menuItemDisplayStr(VendPaymMode);
            break;

        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    CustVendPaymModeTable paymModeTable = CustVendPaymModeTable::findModule(
        moduleCustVend,
        BankBridgingInquiry.PaymMode);

    jumpArgs.lookupRecord(paymModeTable);

    menuFunction = new MenuFunction(menuItemStr, MenuItemType::Display);
    menuFunction.run(jumpArgs);
}
