[Form]
public class BankChequeLayout extends FormRun
{
    private const int Signature1 = 1;
    private const int Signature2 = 2;

}

public void bookRefresh()
{
    FormRun callerForm = element.args().caller();

    if (callerForm && callerForm.name() == formStr(BankAccountTable))
    {
        callerForm.bookRefresh();
    }
}

public void close()
{
    super();
    this.bookRefresh();
}

/// <summary>
/// Enables or disables the custom watermark control.
/// </summary>
private void enableDisableCustomWatermarkField()
{
    if (bankchequeLayout.CopyWatermark == CopyWatermark::Custom)
    {
        bankChequeLayout_BankCustomWatermark.enabled(true);
    }
    else
    {
        bankChequeLayout_BankCustomWatermark.enabled(false);
    }
}

public void init()
{
    if (element.args().dataset() != tableNum(BankAccountTable))
    {
        throw error(Error::missingRecord(this.name()));
    }

    super();
    this.updateDesign();
}

public void setFieldAccess_BR()
{
    if (bankChequeLayout.ChequeFormType == ChequeFormType::BRStyle)
    {
        bankChequeLayout_CustomCheque_BR.allowEdit(true);
    }
    else
    {
        bankChequeLayout_CustomCheque_BR.allowEdit(false);
    }
}

/// <summary>
///     Adjusts the design of the form.
/// </summary>
protected void updateDesign()
{
    if (bankChequeLayout.ChequeFormType == ChequeFormType::ESStyle
        || bankChequeLayout.ChequeFormType == ChequeFormType::CAStyle
        || bankChequeLayout.ChequeFormType == ChequeFormType::DEStyle)
    {
        currency.visible(true);
    }
    else
    {
        currency.visible(false);
    }

    if (bankChequeLayout.ChequeFormType == ChequeFormType::CAStyle
        && !bankChequeLayout.UseGER)
    {
        checkDate.allowEdit(true);

        if (!bankChequeLayout.ChequeDateSeparator_CA)
        {
            bankChequeLayout.ChequeDateSeparator_CA = ChequeDateSeparator_CA::Space;
        }
    }
    else
    {
        checkDate.allowEdit(false);
    }

    if (bankChequeLayout.ChequeFormType == ChequeFormType::FRStyle)
    {
        notes.visible(true);
    }
    else
    {
        notes.visible(false);
    }

    if (bankChequeLayout.ChequeFormType == ChequeFormType::USPayrollStyle)
    {
        printSample.menuItemName(menuitemActionStr(PayrollBankPrintTestCheque));
    }
    else
    {
        printSample.menuItemName(menuitemActionStr(BankPrintTestCheque));
    }

    this.enableDisableCustomWatermarkField();
    this.enableGERFields();
}

private void enableGERFields()
{
    Company.enabled(!bankChequeLayout.UseGER);
    Paper.enabled(!bankChequeLayout.UseGER);
    TopMargin.enabled(!bankChequeLayout.UseGER);
    BottomMargin.enabled(!bankChequeLayout.UseGER);
    CheckPosition.enabled(!bankChequeLayout.UseGER);

    CheckForm.enabled(!bankChequeLayout.UseGER);
    Slipnum.enabled(!bankChequeLayout.UseGER);
    Bank.enabled(!bankChequeLayout.UseGER);
    UseACode.enabled(!bankChequeLayout.UseGER);
    UseBCode.enabled(!bankChequeLayout.UseGER);
    UseCCode.enabled(!bankChequeLayout.UseGER);
    Micr.enabled(!bankChequeLayout.UseGER);

    GER_GERFormatMapping.enabled(bankChequeLayout.UseGER);
    GER_GERNumberOfSlipTxtLines.enabled(bankChequeLayout.UseGER);
}

private void openImageFile(int _signature)
{
    FileUploadTemporaryStorageResult result = File::GetFileFromUser(classstr(ImageFileUploadTemporaryStorageStrategy));

    if (result && result.getUploadStatus())
    {
        str imageFilePathName = result.getDownloadUrl();
        container imageContainer = ImageReference::GetPackedBinaryData(imageFilePathName);

        if (_signature == Signature1)
        {
            bankChequeLayout.Signature1Bmp = imageContainer;
        }
        else
        {
            bankChequeLayout.Signature2Bmp = imageContainer;
        }

        result.deleteResult();
        element.showSignatureImages();
    }
}

private void showSignatureImages()
{
    Image image;

    if (bankChequeLayout.Signature1Bmp)
    {
        image = new Image();
        image.setData(bankChequeLayout.Signature1Bmp);
        signature1LogoBmp.image(image);
    }
    else
    {
        image = new Image();
        signature1LogoBmp.image(image);
    }

    if (bankChequeLayout.Signature2Bmp)
    {
        image = new Image();
        image.setData(bankChequeLayout.Signature2Bmp);
        signature2LogoBmp.image(image);
    }
    else
    {
        image = new Image();
        signature2LogoBmp.image(image);
    }
}

public void run()
{
    super();

    element.showSignatureImages();
}

private void removeImage(int _signature)
{
    if (_signature == Signature1)
    {
        bankChequeLayout.Signature1Bmp = connull();
    }
    else
    {
        bankChequeLayout.Signature2Bmp = connull();
    }

    bankChequeLayout_DS.write();
    bankChequeLayout_DS.executeQuery();
    element.showSignatureImages();
}

public int active()
{
    int ret;

    ret = super();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        element.setFieldAccess_BR();
    }
    // </GBR>

    element.updateDesign();

    return ret;
}

public void modified()
{
    super();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        element.setFieldAccess_BR();
    }
    // </GBR>

    element.updateDesign();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    const str ERModelName = 'Payment generation - check';
    const str ERDataContainer = 'CheckRun';

    return ERFormatMappingTable::lookup(_formReferenceControl, ERModelName, ERDataContainer);
}

public void modified()
{
    super();

    element.updateDesign();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.updateDesign();

    return ret;
}

public void clicked()
{
    super();

    element.openImageFile(Signature1);
}

public void clicked()
{
    super();

    element.removeImage(Signature1);
}

public void clicked()
{
    super();

    element.openImageFile(Signature2);
}

public void clicked()
{
    super();

    element.removeImage(Signature2);
}

/// <summary>
/// Enable or disable the custom watermark field control.
/// </summary>
/// <returns>
/// An integer value returned from super method.
/// </returns>
public int selectionChange()
{
    int ret;
    ret = super();

    element.enableDisableCustomWatermarkField();

    return ret;
}
