void checkBalance()
{
    if (!checkBalance)
        return;

    if ((this.startBalance() + this.paymentInflows() - this.paymentOutflows()) != this.endBalance())
       warning("@GLS115471");

    if (this.totalPayments() != (this.paymentInflows() - this.paymentOutflows()))
        warning("@GLS115472");
}

private void modifiedCheckBoxRange(
    NoYes _checkBoxValue,
    QueryBuildRange _range,
    str _rangeValue)
{
    _range.value(_checkBoxValue ? _rangeValue: SysQuery::valueUnlimited());
    bankClientPayment_ds.executeQuery();
}

