public void init()
{
    LedgerJournalTrans ledgerJournalTrans;

    ;

    if (element.args().record())
    {
        switch (element.args().record().TableId)
        {
            case tableNum(LedgerJournalTrans):
                ledgerJournalTransCaller = true;
                ledgerJournalTrans = element.args().record();
                break;

            case tableNum(BankAccountTable):
                bankAccountCaller = true;
                break;

            case tableNum(BankAccountTrans):
                bankAccountTrans = true;
                break;
        }
        if (element.args().record().TableId == tableNum(LedgerJournalTrans))
        {
            ledgerJournalTransCaller = true;
            ledgerJournalTrans = element.args().record();
        }

        if (element.args().record().TableId == tableNum(BankAccountTable))
        {
            bankAccountCaller = true;
        }
    }

    super();
    qbrUnrecognized.status(RangeStatus::Locked);
    qbrUnreconsiled.status(RangeStatus::Locked);
    qbrPosted.status(RangeStatus::Locked);
    qbrDate.status(RangeStatus::Locked);
    qbrBankAccount.status(RangeStatus::Locked);

    dateFrom.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    dateTo.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    periodFrom  = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    periodTo    = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    if (ledgerJournalTransCaller)
    {
        this.setDesignBankStatement(ledgerJournalTrans);
    }

    if (bankAccountCaller)
    {
        dateFrom.dateValue(dateNull());
        dateTo.dateValue(dateNull());
        periodFrom  = dateNull();
        periodTo    = dateNull();
        stringEditBankAccount.enabled(!bankAccountCaller);
    }

    if (bankAccountTrans)
    {
        dateFrom.dateValue(dateNull());
        dateFrom.enabled(false);
        dateTo.dateValue(dateNull());
        dateTo.enabled(false);
        periodFrom  = dateNull();
        periodTo    = dateNull();
        stringEditBankAccount.enabled(false);
    }

    this.setPeriodQuery();
}

