void setDesignBankStatement(LedgerJournalTrans _ledgerJournalTrans)
{
    ;

    dateFrom.dateValue(dateNull());
    periodFrom = dateNull();

    dateTo.dateValue(_ledgerJournalTrans.TransDate);
    periodTo = _ledgerJournalTrans.TransDate;

    nonReconciled.value(1);
    qbrUnreconsiled.value(queryValue(! nonReconciled.value()));

    stringEditBankAccount.text(LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_ledgerJournalTrans.OffsetLedgerDimension));
}

#define.TwoDots('..')
void setPeriodQuery()
{
    inquiry.enabled(false);

    if (periodFrom || periodTo)
    {
        qbrDate.value(queryValue(periodFrom) + #TwoDots + queryValue(periodTo));
    }
    else
    {
        qbrDate.value("");
    }
    bankClientPayment_ds.executeQuery();
    bankClientAccountBalance_ds.executeQuery();
}

void showTaxes()
{
    LedgerJournalTrans  ledgerJournalTrans;
    TaxCalculationJournal    tax;
    LedgerJournalTable       journalTable;
    LedgerJournalTaxDocument ledgerJournalTaxDocument;

    journalTable.setTmp();
    journalTable.JournalNum = '';
    journalTable.LedgerJournalInclTax = true;
    journalTable.doInsert();

    ledgerJournalTrans.setTmp();
    ledgerJournalTrans.initFromBankClientPayment_RU(bankClientPayment);
    ledgerJournalTrans.insert();

    ledgerJournalTaxDocument = LedgerJournalTaxDocumentTmp_RU::construct(journalTable, ledgerJournalTrans);
    tax = TaxCalculation::newForSourceType(TaxSourceType::Journals, ledgerJournalTaxDocument);
    tax.calculateTax();

    Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkBankClient_RU), tax);
}

