display AmountCur totalPayments()
{
    return inboundPayments - outboundPayments;
}

void updateBalances()
{
    ;

    if (! stringEditBankAccount.valueStr())
    {
        startBalance    = 0;
        endBalance      = 0;
        paymentInflows  = 0;
        paymentOutflows = 0;
        checkBalance    = false;
        return;
    }

    [startBalance, paymentInflows,
     paymentOutflows, endBalance] = BankClientAccountBalance_RU::calcBalance(stringEditBankAccount.valueStr(),
                                                                             periodFrom,
                                                                             periodTo);
     checkBalance = true;
}

void updatePayments()
{
    BankClientPayment_RU paymentTable;
    CompanyBankAccountId accountId;
    ;

    inboundPayments  = 0;
    outboundPayments = 0;

    accountId = stringEditBankAccount.valueStr();

    while select sum(AmountCur) from paymentTable
        group by paymentTable.PaymentDirection
        where paymentTable.AccountID == accountId                    &&
              (! periodFrom || paymentTable.TransDate >= periodFrom) &&
              (! periodTo   || paymentTable.TransDate <= periodTo)
    {
        if (paymentTable.PaymentDirection == BankClientPaymentDirection_RU::Incoming)
        {
            inboundPayments = paymentTable.AmountCur;
        }
        if (paymentTable.PaymentDirection == BankClientPaymentDirection_RU::Outgoing)
        {
            outboundPayments = paymentTable.AmountCur;
        }
    }
}

display NoYes hasPaymentJournal(BankClientPayment_RU _bankClientPayment)
{
    return _bankClientPayment.JournalNum && _bankClientPayment.Voucher;
}

public int active()
{
    int      ret = super();
    boolean  isCurExch = bankClientPayment.isCurrencyExchange();

    bankPaymentOrderJour_Vend.visible(!isCurExch && bankClientPayment.PartnerType == ModuleCustVend::Vend);
    bankPaymentOrderJour_Cust.visible(!isCurExch && bankClientPayment.PartnerType == ModuleCustVend::Cust);

    inquiry.enabled(! bankClientPayment.Unrecognized);
    bankClientEditStatement.enabled(bankClientPayment.Unrecognized);
    ledgerJournalTransDaily.enabled(bankClientPayment.JournalNum && bankClientPayment.Voucher);
    taxTransSource.enabled(bankClientPayment.Prepayment);

    bankClientPayment_ds.allowEdit(!bankClientPayment.Posted);
    bankClientPayment_ds.allowDelete(!bankClientPayment.Posted);

    return ret;
}

