    qbrUnrecognized = qbds.addRange(fieldNum(BankClientPayment_RU, Unrecognized));
    qbrPosted       = qbds.addRange(fieldNum(BankClientPayment_RU, Posted));
    qbrDate         = qbds.addRange(fieldNum(BankClientPayment_RU, TransDate));
    qbrBankAccount  = qbds.addRange(fieldNum(BankClientPayment_RU, AccountID));

    qbrUnreconsiled = qbds.addRange(fieldNum(BankClientPayment_RU, RefTableId));

    //clearDynalinks if called from BankReconciliation_RU form

    if (!bankAccountCaller && !bankAccountTrans)
    {
        this.query().dataSourceTable(this.table()).clearDynalinks();
    }
}

