void clicked()
{
    RTax25OriginalDocuments rTax25OriginalDocuments;
    LedgerJournalTrans      ledgerJournalTrans;
    ;
    select firstonly RecId from ledgerJournalTrans
        where bankClientPayment.JournalNum      == ledgerJournalTrans.JournalNum &&
              bankClientPayment.Voucher         == ledgerJournalTrans.Voucher    &&
              bankClientPayment.TransDate       == ledgerJournalTrans.TransDate;

    if (ledgerJournalTrans.RecId == 0)
    {
        info(strFmt("@GLS104417", bankClientPayment.JournalNum));
    }
    else
    {
        rTax25OriginalDocuments = new RTax25OriginalDocuments(ledgerJournalTrans.TableId, ledgerJournalTrans.RecId);
        rTax25OriginalDocuments.run();
    }
}

