[SuppressBPWarning('BPDeprecatedDataSourceMethod', 'PostR1TODO')]
public void close()
{
    FormDataSource      frmDS;

    super();

    if (transferred)
    {
        frmDS = parentFrmRun.dataSource();
        frmDS.executeQuery();
        frmDS.findRecord(bankCodaAccountStatement);
        frmDS.active();
    }
}

public void enableFields()
{
    bankCodaAccountStatementLines_ds.object(fieldNum(BankCodaAccountStatementLines, LedgerDimension)).enabled(bankCodaAccountStatementLines.AccountType != BankCodaLedgerJournalACType::None);
}

public void init()
{
    if (element.args().dataset() != tableNum(BankCodaAccountStatement))
    {
        throw error ("@SYS22996");
    }
    else
    {
        bankCodaAccountStatement = element.args().record();
    }

    super();

    parentFrmRun = element.args().caller();

    if (bankCodaAccountStatement.DetailsTransferred == NoYes::Yes)
    {
        bankCodaAccountStatementLines_ds.allowEdit(false);
        invoices.enabled(false);
        coda.enabled(false);
    }

    imageList = new ImageListAppl_CustVend();
    hasErrors.imageList(imageList.imageList());
}

[SuppressBPWarning('BPDeprecatedDataSourceMethod', 'PostR1TODO')]
public void refreshAfterInvoiceLink()
{
    BankCodaAccountStatementLines       prevBankCodaAccountStatementLines;

    prevBankCodaAccountStatementLines = BankCodaAccountStatementLines::findRecId(selectedRecord);
    bankCodaAccountStatementLines_ds.research();
    bankCodaAccountStatementLines_ds.findRecord(prevBankCodaAccountStatementLines);
}

/// <summary>
/// Loads the segmented entry control with dimensions for the main account when the main account is changed.
/// </summary>
/// <param name="_segmentedEntry">
/// The segmented entry control that was updated.
/// </param>
/// <param name="_currentMainAccountId">
/// The current main account record ID.
/// </param>
public void segmentValueChangedDefaultDimensions(
    SegmentedEntryControl    _segmentedEntry,
    MainAccountRecId         _currentMainAccountId)
{
    changecompany(_segmentedEntry.parmDataAreaId())
    {
        if (_segmentedEntry.getAccountStructure() != 0)
        {
            MainAccountRecId mainAccountId = _segmentedEntry.getValue(DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount));

            if (mainAccountId && _currentMainAccountId != mainAccountId)
            {
                MainAccountLegalEntity mainAccount = MainAccountLegalEntity::findByMainAccountLegalEntity(mainAccountId, CompanyInfo::current());
                if (mainAccount.DefaultDimension)
                {
                    Map defaultDimensionSpecifiers = LedgerDimensionDefaultingEngine::getDefaultDimensionSpecifiers(mainAccount.DefaultDimension);
                    _segmentedEntry.setDimensionSpecifiers(defaultDimensionSpecifiers);
                }
            }
        }
    }
}

public void write()
{
    super();
    if (bankCodaAccountStatementLines.LedgerDimension
        && bankCodaAccountStatementLines.AccountType == BankCodaLedgerJournalACType::Cust
        && BankCodaParameters::find().CreateNewAccount)
    {
        BankCodaProcessing::createNewCustBankAccount(bankCodaAccountStatementLines);
    }
}

public int active()
{
    int ret;

    ret = super();

    if (bankCodaAccountStatementLines.HasProcessErrors == NoYes::No)
    {
        hasProcessErrors.enabled(false);
        status_Processed.enabled(true);
    }
    else
    {
        hasProcessErrors.enabled(true);
        status_Processed.enabled(false);
    }

    custTrans.enabled(false);
    vendTrans.enabled(false);

    switch (bankCodaAccountStatementLines.AccountType)
    {
        case BankCodaLedgerJournalACType::Cust :
            custTrans.enabled(true);
            break;

        case BankCodaLedgerJournalACType::Vend :
            vendTrans.enabled(true);
            break;
    }

    if (ret)
    {
        element.enableFields();
    }

    currentMainAccountId = MainAccount::findByLedgerDimension(bankCodaAccountStatementLines.LedgerDimension).RecId;

    return ret;
}

public display ImageRes hasErrors(BankCodaAccountStatementLines _bankCodaAccountStatementLines)
{
    if (_bankCodaAccountStatementLines.HasProcessErrors == NoYes::Yes)
    {
        return imageList.image(#ImageError);
    }

    return -1;
}

