[SuppressBPWarning('BPDeprecatedDataSourceMethod', 'PostR1TODO'),
 SuppressBPWarning('BPDeprecatedFormMethod', 'PostR1TODO')]
public void clicked()
{
    BankCodaAccountStatementLines       prevBankCodaAccountStatementLines;

    prevBankCodaAccountStatementLines.data(bankCodaAccountStatementLines);
    super();

    element.reload(element.args());
    bankCodaAccountStatementLines_ds.findRecord(prevBankCodaAccountStatementLines);
}

public void clicked()
{
    if (BankCodaAccountStatementLines::notAllProcessedForStatementWithBankAccount(
        bankCodaAccountStatement.AccountStatementNum,
        bankCodaAccountStatement.AccountStatementDate,
        bankCodaAccountStatement.AccountId))
    {
        codaTransfers.enabled(false);
        codaProcessings.enabled(true);
    }
    else
    {
        bankCodaAccountStatement.reread();
        if (bankCodaAccountStatement.DetailsTransferred == NoYes::No)
        {
            codaTransfers.enabled(true);
        }
        else
        {
            codaTransfers.enabled(false);
        }
        codaProcessings.enabled(false);
    }
    super();
}

[SuppressBPWarning('BPDeprecatedDataSourceMethod', 'PostR1TODO')]
public void clicked()
{
    BankCodaAccountStatementLines       bankCodaAccountStatementLinesCurrent;

    bankCodaAccountStatementLinesCurrent.data(bankCodaAccountStatementLines);
    super();

    bankCodaAccountStatementLines_ds.executeQuery();
    bankCodaAccountStatementLines_ds.findRecord(bankCodaAccountStatementLinesCurrent);
    bankCodaAccountStatementLines.update();
}

