public void lookup()
{
    switch (bankCodaAccountStatementLines.AccountType)
    {
        case BankCodaLedgerJournalACType::Cust:
            CustTable::lookupCustomer(this);
            break;
        case BankCodaLedgerJournalACType::Vend:
            VendTable::lookupVendor(this);
            break;
        default:
            super();
            break;
    }
}

public boolean checkUseCustomLookup(int _accountTypeEnumValue, int _secondaryAccountTypeEnumValue)
{
    boolean returnValue;

    switch (_accountTypeEnumValue)
    {
        case BankCodaLedgerJournalACType::Cust:
        case BankCodaLedgerJournalACType::Vend:
            returnValue = true;
            break;
        default:
            returnValue = false;
            break;
    }

    return returnValue;
}

