public void clicked()
{
    super();

    Bank_JP::validateBankNameKana(
        BankGroup.NameKana_JP,
        tableNum(BankGroup),
        fieldnum(BankGroup, NameKana_JP));
    Bank_JP::validateBankNameKana(
        BankGroup.BranchNameKana_JP,
        tableNum(BankGroup),
        fieldnum(BankGroup, BranchNameKana_JP));
}
