[Form]
public class BankLCExport extends FormRun
{
    BankLCExportFeatureChecker bankLCExportFeatureChecker;
    SalesTable                 salesTableOriginal;

}

/// <summary>
/// Configs datasource and controls based on the Letter of Credit Status.
/// </summary>
public void configByStatus()
{
    boolean isCreated = true;

    if (bankLCExport.RecId)
    {
        isCreated = bankLCExport.isPreparing();

        bankLCExport_DS.allowDelete(isCreated);
        bankLCInfo_DS.allowDelete(bankLCExport_DS.allowDelete());
        bankLCExportLine_DS.allowCreate(isCreated);
        bankLCExportLine_DS.allowDelete(isCreated);
        buttonFetchShipmentLines.enabled(isCreated);
        buttonLineNew.enabled(bankLCExportLine_DS.allowCreate());
        ButtonLineDelete.enabled(BankLCExportLine_DS.allowDelete());
        MenuItemButtonPrintLCDetails.enabled(true);
        ButtonIssue.enabled(bankLCExport.isPreparing());
        salesTable_ds.allowDelete(isCreated && !TabPageGrid.isActivePage());
    }
    else
    {
        bankLCExport_DS.allowEdit(false);
        bankLCExport_DS.allowDelete(false);
        bankLCInfo_DS.allowEdit(false);
        bankLCInfo_DS.allowDelete(false);
        bankLCExportLine_DS.allowCreate(false);
        bankLCExportLine_DS.allowDelete(false);
        bankLCExportLine_DS.allowEdit(false);
        buttonFetchShipmentLines.enabled(false);
        buttonLineNew.enabled(false);
        ButtonLineDelete.enabled(false);
        MenuItemButtonPrintLCDetails.enabled(false);
        ButtonIssue.enabled(false);
        salesTable_ds.allowDelete(false);
    }

    element.setTitleText();
}

/// <summary>
/// Configs the Letter of Credit line controls based on line status.
/// </summary>
public void configLinesControl()
{
    switch (bankLCExportLine.ExportShipmentStatus)
    {
        case BankLCExportShipmentStatusType::Open :
            bankLCExportLine_ds.object(fieldnum(BankLCExportLine, ShipmentAmountCur)).allowEdit(true);
            bankLCExportLine_ds.object(fieldnum(BankLCExportLine, ActualMaturityDateTime)).allowEdit(true);
            bankLCExportLine_ds.object(fieldnum(BankLCExportLine, DocumentSubmitted)).allowEdit(true);
            break;
        case BankLCExportShipmentStatusType::Shipped :
        case BankLCExportShipmentStatusType::Invoiced :
            bankLCExportLine_ds.object(fieldnum(BankLCExportLine, ShipmentAmountCur)).allowEdit(false);
            bankLCExportLine_ds.object(fieldnum(BankLCExportLine, ActualMaturityDateTime)).allowEdit(true);
            bankLCExportLine_ds.object(fieldnum(BankLCExportLine, DocumentSubmitted)).allowEdit(true);
            break;
        case BankLCExportShipmentStatusType::PaymentReceived :
            bankLCExportLine_ds.object(fieldnum(BankLCExportLine, ShipmentAmountCur)).allowEdit(false);
            bankLCExportLine_ds.object(fieldnum(BankLCExportLine, ActualMaturityDateTime)).allowEdit(false);
            bankLCExportLine_ds.object(fieldnum(BankLCExportLine, DocumentSubmitted)).allowEdit(false);
            break;
    }
}

/// <summary>
/// Creates and inserts Letter of Credit record.
/// </summary>
/// <param name="_salesTable">
/// The sales order
/// </param>
public void createLCFromSalesTable(SalesTable _salesTable)
{
    BankLCExport        bankLCExportLocal;

    bankLCExportLocal = BankLCExport::findBySalesTable(_salesTable.SalesId);

    if (bankLCExportLocal.RecId == 0)
    {
        BankLCExportType::createBankLCExport(_salesTable.SalesId);
    }
}

public void init()
{
    QueryBuildRange queryBuildRange;

    bankLCExportFeatureChecker = BankLCExportFeatureChecker::construct();
    if (!bankLCExportFeatureChecker.checkFeatureEnabled(false))
    {
        Box::stop(bankLCExportFeatureChecker.getCheckFailedMessage());
        this.lifecyclehelper().cancelFormInitialization(Exception::Error, bankLCExportFeatureChecker.getCheckFailedMessage());
        return;
    }

    super();
    queryBuildRange = SalesTable_ds.queryBuildDataSource().addRange(fieldnum(SalesTable, BankDocumentType));
    queryBuildRange.value(SysQuery::value(BankDocumentType::LetterOfCredit));

    queryBuildRange = SalesTable_ds.queryBuildDataSource().addRange(fieldnum(SalesTable, BankDocumentType));
    queryBuildRange.value(SysQuery::value(BankDocumentType::ImportCollection));

    BankLCExport_ds.addFieldToSelectionList(fieldNum(bankLCExport, ExportStatus));
}

/// <summary>
/// Refreshs UI after the action has been done.
/// </summary>
public void refreshAfterActionDone()
{
    salesTable_ds.research(true);
    this.configByStatus();

    if (bankLCExport.RecId)
    {
        bankLCExport.reread();
    }
}

public void setTitleText()
{
    if (bankLCExport.RecId != 0)
    {
        headerTitle.visible(true);
        headerTitle.text(
            strfmt(
                '%1 : %2 - %3',
                bankLCExport.SalesTable,
                bankLCExport.getBankDocumentType(),
                bankLCExport.ExportStatus));
    }
    else
    {
        headerTitle.visible(false);
    }
}

public int task(int _taskId)
{
    #Task
    int ret;

    switch (_taskId)
    {
        case #taskAttachments:
            if (bankLCExport.RecId)
            {
                ret = super(_taskId);
            }
            break;
        default:
            ret = super(_taskId);
            break;
    }

    return ret;
}

public void initValue()
{
    super();
    bankLCExportLine.BankLC = bankLCExport.RecId;
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.configLinesControl();
    }

    return ret;
}

