public void modified()
{
    super();
    bankLCExport_DS.cacheCalculateMethod(tablemethodstr(BankLC, displayBankLCAmount));
    bankLCExport_DS.cacheCalculateMethod(tablemethodstr(BankLC, displayBankLCBalanceAmountCur));
}

public void write()
{
    // SalesTable should not be edit at this form. Turn to save the updates on BankLCExport
    bankLCExport_ds.write();
}

public void linkActive()
{
    SalesTable salesTableLocal;

    if (element.args().dataset() != tablenum(SalesTable)
        || element.args().record().RecId == 0)
    {
        // The order is not found.
        throw error("@SYS316271");
    }

    salesTableLocal = element.args().record();

    if (salesTableLocal.isMarkedForBankLC())
    {
        element.createLCFromSalesTable(salesTableLocal);
    }

    super();

    element.configByStatus();
}

public int active()
{
    int ret;

    if (BankLCExport.RecId == 0 && salesTable.isMarkedForBankLC())
    {
        element.createLCFromSalesTable(salesTable);
        this.research(true);
    }

    ret = super();

    if (ret)
    {
        element.configByStatus();
    }

    return ret;
}

public void delete()
{
    BankLCExport_ds.delete();
}

void clicked()
{
    boolean ret;

    ret = BankLCExportType::canIssueLC(bankLCExport.RecId);

    if (ret)
    {
        BankLCExport::issueLC(bankLCExport.RecId);
        element.refreshAfterActionDone();
    }
}

void clicked()
{
    if (bankLCExport.RecId == 0)
    {
        return;
    }

    super();
}

public void tabChanged(int _FromTab, int _ToTab)
{
    super(_FromTab, _ToTab);

    // Buttons to show in Grid View
    EditDetailsButton.visible(TabPageGrid.isActivePage());

    // Buttons to show in Details View
    if (TabPageGrid.isActivePage())
    {
        element.design().ViewEditMode(ViewEditMode::View);
    }
    else
    {
        element.design().ViewEditMode(ViewEditMode::Auto);
    }
    BankLCExport_DS.allowDelete(!TabPageGrid.isActivePage());

    // Not allow to delete letter of credit in Grid View
    salesTable_ds.allowDelete(bankLCExport.RecId && bankLCExport.isPreparing() && !TabPageGrid.isActivePage());
}

void clicked()
{
    super();
    element.refreshAfterActionDone();
}
