public class BankLCImport extends FormRun
{
    BankDocumentAgreeLnLookupController     agreementLineLookupController;
    BankLCImportFeatureChecker              bankLCImportFeatureChecker;
    // <GSA>
    BankLCImportChargeFeatureChecker_SA     BankLCImportChargeFeatureChecker_SA;
    // </GSA>
}

/// <summary>
/// Config the controls based on the value of <c>BankDocumentType</c> of the Letter of Credit.
/// </summary>
/// <param name="_bankDocumentType">
/// The <c>BankDocumentType</c> of LC
/// </param>
public void configByBankDocumentType(BankDocumentType _bankDocumentType)
{
    boolean isEnable = true;

    if (_bankDocumentType == BankDocumentType::ImportCollection)
    {
        isEnable = false;
    }

    tabPageBankDetails.visible(isEnable);
    bankLCImport_DS.object(fieldNum(BankLCImport, ImportAdvisingBank)).mandatory(isEnable);
}

/// <summary>
/// Configs datasource and controls based on the Letter of Credit status.
/// </summary>
public void configByStatus()
{
    boolean isCreated = true;

    if (bankLCImport.RecId)
    {
        isCreated = bankLCImport.isCreated();

        bankLCImport_DS.allowEdit(isCreated);
        bankLCImport_DS.allowDelete(isCreated);
        bankLCInfo_DS.allowEdit(isCreated);
        bankLCInfo_DS.allowDelete(isCreated);
        bankLCImportLine_DS.allowCreate(isCreated);
        bankLCImportLine_DS.allowDelete(isCreated);
        bankLCImportLine_DS.allowEdit(!bankLCImport.isClosed());
        purchTableReference_DS.allowDelete(isCreated && !tabPageGrid.isActivePage());

        buttonAmendment.enabled(bankLCImport.isConfirmed());
        buttonConfirm.enabled(isCreated);
        buttonClose.enabled(bankLCImport.isConfirmed() && !bankLCImport.isClosed());
        buttonFetch.enabled(isCreated);
        buttonLineNew.enabled(bankLCImportLine_DS.allowCreate());
        menuItemButtonHistoryInquiry.enabled(true);
        menuItemButtonPrintLCApplication.enabled(true);

        menuItemButtonMargin.enabled(!isCreated);

        // <GSA>
        if (bankLCImportChargeFeatureChecker_SA.checkFeatureEnabled(false))
        {
            menuItemButtonCharge_SA.enabled(!isCreated);
        }
        // </GSA>

        bankLCImport_ToleranceAmountCur.visible(isCreated);
    }
    else
    {
        bankLCImport_DS.allowEdit(false);
        bankLCImport_DS.allowDelete(false);
        bankLCInfo_DS.allowEdit(false);
        bankLCInfo_DS.allowDelete(false);
        bankLCImportLine_DS.allowCreate(false);
        bankLCImportLine_DS.allowDelete(false);
        bankLCImportLine_DS.allowEdit(false);
        purchTableReference_DS.allowDelete(false);

        buttonAmendment.enabled(false);
        buttonConfirm.enabled(false);
        buttonClose.enabled(false);
        buttonFetch.enabled(false);
        buttonLineNew.enabled(false);

        menuItemButtonHistoryInquiry.enabled(false);
        menuItemButtonPrintLCApplication.enabled(false);

        menuItemButtonMargin.enabled(false);
        // <GSA>
        if (bankLCImportChargeFeatureChecker_SA.checkFeatureEnabled(false))
        {
            menuItemButtonCharge_SA.enabled(false);
        }
        // </GSA>
    }

    element.setTitleText();
}

/// <summary>
/// Enables/disables field DeferredDays.
/// </summary>
public void configDeferredDays()
{
    if (bankLCInfo.Draft == BankLCDraftType::Acceptance)
    {
        bankLCInfo_DS.object(fieldNum(BankLCInfo, Deferreddays)).enabled(true);
    }
    else
    {
        bankLCInfo_DS.object(fieldNum(BankLCInfo, Deferreddays)).enabled(false);
    }
}

/// <summary>
/// Configs the Letter of Credit line controls based on line status.
/// </summary>
public void configLinesControl()
{
    switch (bankLCImportLine.ImportShipmentStatus)
    {
        case BankLCImportShipmentStatusType::Created :
            bankLCImportLine_ds.object(fieldNum(BankLCImportLine, ShipmentAmountCur)).allowEdit(true);
            bankLCImportLine_ds.object(fieldNum(BankLCImportLine, ExpectedMaturityDateTime)).allowEdit(true);
            bankLCImportLine_ds.object(fieldNum(BankLCImportLine, ActualMaturityDateTime)).allowEdit(true);
            break;
        case BankLCImportShipmentStatusType::Open :
        case BankLCImportShipmentStatusType::PackingSlipUpdated :
        case BankLCImportShipmentStatusType::InvoiceUpdated :
            bankLCImportLine_ds.object(fieldNum(BankLCImportLine, ShipmentAmountCur)).allowEdit(false);
            bankLCImportLine_ds.object(fieldNum(BankLCImportLine, ExpectedMaturityDateTime)).allowEdit(false);
            bankLCImportLine_ds.object(fieldNum(BankLCImportLine, ActualMaturityDateTime)).allowEdit(true);
            break;
        case BankLCImportShipmentStatusType::Paid :
            bankLCImportLine_ds.object(fieldNum(BankLCImportLine, ShipmentAmountCur)).allowEdit(false);
            bankLCImportLine_ds.object(fieldNum(BankLCImportLine, ExpectedMaturityDateTime)).allowEdit(false);
            bankLCImportLine_ds.object(fieldNum(BankLCImportLine, ActualMaturityDateTime)).allowEdit(false);
            break;
    }
    buttonLineDelete.enabled(bankLCImportLine_DS.allowDelete());
}

/// <summary>
/// Creates and inserts Letter of Credit record
/// </summary>
/// <param name="_purchTable">
/// The purchase order
/// </param>
public void createLCFromPurchTable(PurchTable _purchTable)
{
    BankLCImport bankLCImportLocal;

    bankLCImportLocal = BankLCImport::findByPurchTable(_purchTable.PurchId);

    if (bankLCImportLocal.RecId == 0)
    {
        BankLCImportType::createBankLCImport(_purchTable.PurchId);
    }
}

/// <summary>
/// Gets the <c>BankDocumentAgreementLineRefRecId</c> from the lookup controller.
/// </summary>
/// <returns>
/// The record Id of the <c>BankDocumentFacilityAgreementLine</c>
/// </returns>
public BankDocumentAgreementLineRefRecId getAgreementLineBasedOnLookupValue()
{
    BankDocumentAgreementLineRefRecId agreementLineRecId;

    agreementLineRecId = agreementLineLookupController.parmBankDocumentAgreementLineRecId();
    if (agreementLineRecId != 0)
    {
        bankLCImport.BankDocumentFacilityAgreementLine = agreementLineRecId;
        agreementLineLookupController.parmBankDocumentAgreementLineRecId(0);
    }

    return agreementLineRecId;
}

public void init()
{
    QueryBuildRange queryBuildRange;

    bankLCImportFeatureChecker = BankLCImportFeatureChecker::construct();
    if (!bankLCImportFeatureChecker.checkFeatureEnabled(false))
    {
        Box::stop(bankLCImportFeatureChecker.getCheckFailedMessage());
        this.lifecyclehelper().cancelFormInitialization(Exception::Error, bankLCImportFeatureChecker.getCheckFailedMessage());
        return;
    }

    super();
    agreementLineLookupController = BankDocumentAgreeLnLookupController::construct();

    // <GSA>
    bankLCImportChargeFeatureChecker_SA = BankLCImportChargeFeatureChecker_SA::construct(false);

    if (bankLCImportChargeFeatureChecker_SA.checkFeatureEnabled(false))
    {
        menuItemButtonCharge_SA.visible(true);
        menuItemButtonViewCharge_SA.visible(true);
    }
    else
    {
        menuItemButtonCharge_SA.visible(false);
        menuItemButtonViewCharge_SA.visible(false);
    }
    // </GSA>

    queryBuildRange = PurchTableReference_ds.queryBuildDataSource().addRange(fieldnum(PurchTable, BankDocumentType));
    queryBuildRange.value(SysQuery::value(BankDocumentType::LetterOfCredit));

    queryBuildRange = PurchTableReference_ds.queryBuildDataSource().addRange(fieldnum(PurchTable, BankDocumentType));
    queryBuildRange.value(SysQuery::value(BankDocumentType::ImportCollection));

    BankLCImport_ds.addFieldToSelectionList(fieldNum(BankLCImport, ImportStatus));
}

/// <summary>
/// Performs lookup for <c>BankDocumentFacilityAgreementLine</c>
/// </summary>
/// <param name="_control">
/// The control to require lookup
/// </param>
/// <param name="_callerTableName">
/// The caller table name.
/// </param>
/// <param name="_callerFieldName">
/// The caller field name.
/// </param>
public void performAgreementLineLookup(
    FormStringControl   _control,
    TableName           _callerTableName,
    FieldName           _callerFieldName)
{
    agreementLineLookupController.parmBankDocumentAgreementLineRecId(bankLCImport.BankDocumentFacilityAgreementLine);
    agreementLineLookupController.parmApplicationDateTime(bankLCImport.ApplicationDateTime);
    agreementLineLookupController.parmBankDocumentType(bankLCImport.getBankDocumentType());
    agreementLineLookupController.parmCurrencyCode(bankLCImport.getPurchCurrencyCode());
    agreementLineLookupController.parmCallerTableName(_callerTableName);
    agreementLineLookupController.parmCallerFieldName(_callerFieldName);
    agreementLineLookupController.performLookup(_control);
}

/// <summary>
/// Refreshs UI after the action has been done.
/// </summary>
public void refreshAfterActionDone()
{
    purchTableReference_ds.research(true);

    if (bankLCImport.RecId)
    {
        bankLCImport.reread();
    }

    element.configByStatus();
}

public boolean refreshAgreementUI()
{
    boolean ret = true;

    ret = bankLCImport_DS.cacheCalculateMethod(tableMethodStr(BankLCImport, displayAgreementNumber));
    if (ret)
    {
        bankLCImport_DS.cacheCalculateMethod(tableMethodStr(BankLCImport, displayFacilityBalance));
    }

    return ret;
}

public void setTitleText()
{
    if (bankLCImport.RecId != 0)
    {
        headerTitle.visible(true);
        headerTitle.text(
            strFmt(
                '%1 : %2 - %3',
                bankLCImport.PurchTable,
                bankLCImport.getBankDocumentType(),
                bankLCImport.ImportStatus));
    }
    else
    {
        headerTitle.visible(false);
    }
}

/// <summary>
/// The edit method for the Bank document facility type name of the Letter of Credit.
/// </summary>
/// <param name="_set">
/// A Boolean value that indicates whether to set or read the value.
/// </param>
/// <param name="_bankLCImport">
/// The record the edit method should action for.
/// </param>
/// <param name="_value">
/// The value set from the form control.
/// </param>
/// <returns>
/// The value should be displayed on the form control.
/// </returns>
edit BankDocumentFacilityTypeName editBankDocumentFacilityTypeName(
    boolean                         _set,
    BankLCImport                    _bankLCImport,
    BankDocumentFacilityTypeName    _value)
{
    BankDocumentAgreementLineRefRecId agreementLineRecId;

    if (_set)
    {
        agreementLineRecId = element.getAgreementLineBasedOnLookupValue();
        if (agreementLineRecId == 0)
        {
            if (_bankLCImport.changeBankDocumentFacilityAgreementLine("", _value))
            {
                return _value;
            }
        }
    }

    return _bankLCImport.getBankDocumentFacilityTypeName();
}

/// <summary>
/// The edit method for the Bank account of the Letter of Credit.
/// </summary>
/// <param name="_set">
/// A Boolean value that indicates whether to set or read the value.
/// </param>
/// <param name="_bankLCImport">
/// The record the edit method should action for.
/// </param>
/// <param name="_value">
/// The value set from the form control.
/// </param>
/// <returns>
/// The value should be displayed on the form control.
/// </returns>
edit CompanyBankAccountId editBankAccountId(
    boolean               _set,
    BankLCImport          _bankLCImport,
    CompanyBankAccountId  _value)
{
    BankDocumentAgreementLineRefRecId agreementLineRecId;

    if (_set)
    {
        agreementLineRecId = element.getAgreementLineBasedOnLookupValue();
        if (agreementLineRecId == 0)
        {
            if (_bankLCImport.changeBankDocumentFacilityAgreementLine(_value, ""))
            {
                return _value;
            }
        }
    }

    return _bankLCImport.getBankAccount();
}

