public void lookup(FormControl _formControl, str _filterStr)
{
    if (bankLCImport.IssueDateTime)
    {
        VendBankAccount::lookupBankAccount(_formControl,
                                            bankLCImport.getVendAccount(),
                                            curext(),
                                            bankLCImport.IssueDateTime);
    }
    else
    {
        VendBankAccount::lookupBankAccount(_formControl,
                                            bankLCImport.getVendAccount(),
                                            curext(),
                                            DateTimeUtil::utcNow());
    }
}

