public int active()
{
    int ret;

    if (bankLCImport.RecId == 0 && purchTableReference.isMarkedForBankLC())
    {
        element.createLCFromPurchTable(purchTableReference);
        this.research(true);
    }

    ret = super();

    if (ret)
    {
        element.configByBankDocumentType(bankLCImport.getBankDocumentType());
        element.configByStatus();
    }

    return ret;
}

public void linkActive()
{
    PurchTable purchTableLocal;

    if (element.args().dataset() != tablenum(PurchTable)
        || element.args().record().RecId == 0)
    {
        // The order is not found.
        throw error("@SYS316271");
    }

    purchTableLocal = element.args().record();

    if (purchTableLocal.isMarkedForBankLC())
    {
        element.createLCFromPurchTable(purchTableLocal);
    }

    super();

    element.configByStatus();
}

