void clicked()
{
    if (bankLCImport.RecId == 0)
    {
        return;
    }
    // If the Letter of Credit is closed, the Letter of Credit could not be re-opened. Would you like to continue?
    if (Box::okCancel("@SYS316194", DialogButton::Cancel) == DialogButton::Ok)
    {
        super();
        element.refreshAfterActionDone();
    }
}

