public void tabChanged(int _fromTab, int _toTab)
{
    super(_fromTab, _toTab);

    // Buttons to show in Grid View
    editDetailsButton.visible(tabPageGrid.isActivePage());

    // Buttons to show in Details View
    if (tabPageGrid.isActivePage())
    {
        element.design().viewEditMode(ViewEditMode::View);
    }
    else
    {
        element.design().viewEditMode(ViewEditMode::Auto);
    }
    BankLCImport_DS.allowDelete(!tabPageGrid.isActivePage());

    // Not allow to delete letter of credit in Grid View
    purchTableReference_DS.allowDelete(bankLCImport.RecId && bankLCImport.isCreated() && !tabPageGrid.isActivePage());
}

