[Form]
public class BankLCImportHistory extends FormRun
{
    BankLCImportFeatureChecker bankLCImportFeatureChecker;
}

/// <summary>
/// Builds the query.
/// </summary>
private void buildQuery()
{
    QueryBuildDataSource queryBuildDataSource;

    queryBuildDataSource = SysQuery::findOrCreateDataSource(
        BankLCImportHistory_DS.query(),
        tablenum(BankDocumentFacilityView));

    queryBuildDataSource.addLink(
        fieldnum(BankLCImportHistory, BankDocumentFacilityAgreementLine),
        fieldnum(BankDocumentFacilityView, BankDocumentFacilityAgreementLine));
}

public void init()
{
    bankLCImportFeatureChecker = BankLCImportFeatureChecker::construct();
    if (!bankLCImportFeatureChecker.checkFeatureEnabled(false))
    {
        Box::stop(bankLCImportFeatureChecker.getCheckFailedMessage());
        this.lifecyclehelper().cancelFormInitialization(Exception::Error, bankLCImportFeatureChecker.getCheckFailedMessage());
        return;
    }

    if (!this.args().caller())
    {
        Box::stop("@SYS313677");
        this.lifecyclehelper().cancelFormInitialization(Exception::Error, "@SYS313677");
        return;
    }

    super();
    element.buildQuery();
}

public void setTitleText()
{
    if (bankLCImportHistory.RecId != 0)
    {
        headerTitle.visible(true);
        headerTitle.text(
            strfmt(
                '%1 : %2 - %3 %4',
                bankLCImport.PurchTable,
                bankLCImport.BankLCNumber,
                "@SYS316148",
                bankLCImportHistory.Version));
    }
    else
    {
        headerTitle.visible(false);
    }
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.setTitleText();
    }

    return ret;
}
