Method.Source:
[Form]
public class BankLGGuarantee extends FormRun
{
    BankLGType          bankLGType;

    boolean isFinTagConfigurationSetForCurrent = FinTagConfiguration::isFinTagConfigurationSet(curExt());

}

/// <summary>
///     Does some updates when some action is done successfully done on current LG.
/// </summary>
public void actionDone()
{
    bankLGGuarantee_ds.reread();
    bankLGGuarantee_ds.refresh();
    bankLGGuarantee_ds.active();
}

/// <summary>
///     Display the currency code of Letter of Guarantee.
/// </summary>
/// <returns>
///     The currency code.
/// </returns>
display public BankLGCurrencyCode displayCurrencyCode()
{
    BankLGCurrencyCode    currencyCode;

    if (BankDocumentFacilityView.RecId)
    {
        currencyCode = BankDocumentFacilityView.CurrencyCode;
    }
    else if (BankLGDocumentView.RecId)
    {
        currencyCode = BankLGDocumentView.CurrencyCode;
    }

    return currencyCode;
}

/// <summary>
///     Gets the instance of <c>BankLGType</c>.
/// </summary>
/// <returns>
///     The instance of <c>BankLGType</c>.
/// </returns>
public BankLGType getBankLGType()
{
    return bankLGType;
}

public void init()
{
    super();
    // Manually add dynamic link from BankDocumentFacilityView to BankLGGuarantee
    BankDocumentFacilityView_ds.query().dataSourceTable(tablenum(BankDocumentFacilityView)).addDynalink(
        fieldnum(BankDocumentFacilityView, BankDocumentFacilityAgreementLine),
        bankLGGuarantee,
        fieldnum(BankLGGuarantee, FacilityAgreementLine));

    // Manually add relationship from BankLGGuarantee to BankLGDocumentView
    BankLGDocumentView_ds.query().dataSourceTable(tablenum(BankLGDocumentView)).addLink(
        fieldnum(BankLGGuarantee, RecId),
        fieldnum(BankLGDocumentView, Guarantee));

    bankLGType = new BankLGType();
    bankLGType.parmBankLG(bankLGGuarantee);
}

/// <summary>
///     Sets the status of Letter of Guarantee action related buttons based on the current status of action.
/// </summary>
public void setBankLGActionButtonAccess()
{
    bankLGAction_ds.allowDelete(bankLGAction.validateExecuteDelete());
    buttonLineActionRemove.enabled(bankLGAction_ds.allowDelete());
}

/// <summary>
///     Sets the status of Letter of Guarantee related buttons based on the current status of Letter of Guarantee.
/// </summary>
public void setBankLGButtonAccess()
{
    actionSubmitToBank.enabled(bankLGType.isActionAvailable(BankLGAllActionType::ExecuteSubmitToBank));
    actionReceiveFromBank.enabled(bankLGType.isActionAvailable(BankLGAllActionType::ExecuteReceiveFromBank));
    actionGiveToBeneficiary.enabled(bankLGType.isActionAvailable(BankLGAllActionType::ExecuteGiveToBeneficiary));
    actionIncreaseValue.enabled(bankLGType.isActionAvailable(BankLGAllActionType::ExecuteIncreaseValue));
    actionDecreaseValue.enabled(bankLGType.isActionAvailable(BankLGAllActionType::ExecuteDecreaseValue));
    actionExtend.enabled(bankLGType.isActionAvailable(BankLGAllActionType::ExecuteExtend));
    actionCancel.enabled(bankLGType.isActionAvailable(BankLGAllActionType::ExecuteCancel));
    actionLiquidate.enabled(bankLGType.isActionAvailable(BankLGAllActionType::ExecuteLiquidate));

    bankLGGuarantee_ds.allowDelete(bankLGGuarantee.mayBeDeleted());
}

public int active()
{
    int     ret;

    ret = super();

    if (ret)
    {
        bankLGType.checkExpiryDate();
        element.setBankLGButtonAccess();
        TabFinancialTagLine.visible(isFinTagConfigurationSetForCurrent);
    }

    return ret;
}

public int active()
{
    int     ret;

    ret = super();

    if (ret)
    {
        element.setBankLGActionButtonAccess();
    }

    return ret;
}

public void delete()
{
    BankLGAction.executeDelete();
    bankLGGuarantee_ds.reread();
    if (bankLGGuarantee.RecId == 0)
    {
        bankLGGuarantee_ds.research();
    }
    else
    {
        bankLGGuarantee_ds.refresh();
        element.setBankLGButtonAccess();

        bankLGAction_DS.reread();
        bankLGAction_DS.refresh();
        element.setBankLGActionButtonAccess();
    }
}

/// <summary>
/// Serves as the jump reference for the original ledger journal table.
/// </summary>
public void jumpRef()
{
    Args args;
    MenuFunction menuFunction;

    LedgerJournalTable ledgerJournalTable = LedgerJournalTable::find(BankLGAction.LedgerJournalTable);

    args = new Args(formStr(LedgerJournalTable));

    // Use args.lookupRecord() because the implicit record() filtering doesn't work when a datasource has a self-reference.
    args.lookupRecord(ledgerJournalTable);
    args.caller(element);

    menuFunction = new MenuFunction(menuitemDisplayStr(LedgerJournalTable), MenuItemType::Display);
    menuFunction.run(args);
}
