Method.Source:
[Form]
public class BankLGRequestForm extends FormRun
{
    BankLGType          bankLGType;
}

/// <summary>
///     Does some updates when some action is done successfully done on current LG.
/// </summary>
public void actionDone()
{
    if (bankLGGuarantee.RecId == 0)
    {
        // The "Request" action will create a new LG.
        bankLGGuarantee_ds.research();
    }
    else
    {
        bankLGGuarantee_ds.reread();
        bankLGGuarantee_ds.refresh();
        bankLGGuarantee_ds.active();
    }
}

/// <summary>
///     The display method to display the currency code of Letter of Guarantee.
/// </summary>
/// <returns>
///     The currency code.
/// </returns>
display public BankLGCurrencyCode displayCurrencyCode()
{
    BankLGCurrencyCode  currencyCode;

    if (BankLGGuarantee.RecId && BankLGGuarantee.FacilityAgreementLine)
    {
        currencyCode = BankLGGuarantee.getBankDocumentFacilityAgreementLine().getBankDocumentFacilityAgreement().displayCurrencyCode();
    }
    else if (BankLGDocumentView.RecId)
    {
        currencyCode = BankLGDocumentView.CurrencyCode;
    }

    return currencyCode;
}

