/// <returns>
///     The instance of <c>BankLGType</c>.
/// </returns>
public BankLGType getBankLGType()
{
    return bankLGType;
}

/// <summary>
///     Gets the origin document.
/// </summary>
/// <returns>
///     The instance of origin document.
/// </returns>
public BankLGDocumentMap getOriginDocument()
{
    return this.args().record();
}

public void init()
{
    BankLGFeatureChecker::checkBankLGEnabled(true);

    super();

    // Check the caller
    if (element.args().caller()
        && element.args().record().RecId != 0)
    {
        switch (element.args().dataset())
        {
            case tablenum(SalesTable):
            case tablenum(PurchTable):
            case tablenum(SalesQuotationTable):
            case tablenum(ProjTable):
                break;
            default:
                // The parameter from caller is illegal.
                throw error("@SYS317938");
        }
    }
    else
    {
        // Window must be called with parameters.
        throw error("@SYS22539");
    }

    // Manually add relationship from BankLGGuarantee to BankLGDocumentView
    BankLGDocumentView_ds.query().dataSourceTable(tablenum(BankLGDocumentView)).addLink(
        fieldnum(BankLGGuarantee, RecId),
        fieldnum(BankLGDocumentView, Guarantee));

    bankLGType = new BankLGType();
    bankLGType.parmBankLG(bankLGGuarantee);
}

/// <summary>
///     Sets the title text.
/// </summary>
public void setTitleText()
{
    if (bankLGGuarantee.RecId)
    {
        headerTitle.text(
            strfmt(
                '%1 : %2 - %3',
                bankLGGuarantee.Number,
                bankLGDocumentView.RequestAccount,
                enum2Value(bankLGDocumentView.AccountType)));
    }
    else
    {
        headerTitle.text('');
    }
}

/// <summary>
///     Updates the status of all actions based on the current status of Letter of Guarantee.
/// </summary>
public void updateActionStatus()
{
    actionRequest.enabled(bankLGType.isActionAvailable(BankLGAllActionType::Request));
    actionGiveToBeneficiary.enabled(bankLGType.isActionAvailable(BankLGAllActionType::RequestGiveToBeneficiary));
    actionIncreaseValue.enabled(bankLGType.isActionAvailable(BankLGAllActionType::RequestIncreaseValue));
    actionDecreaseValue.enabled(bankLGType.isActionAvailable(BankLGAllActionType::RequestDecreaseValue));
    actionExtend.enabled(bankLGType.isActionAvailable(BankLGAllActionType::RequestExtend));
    actionCancel.enabled(bankLGType.isActionAvailable(BankLGAllActionType::RequestCancel));
    actionLiquidate.enabled(bankLGType.isActionAvailable(BankLGAllActionType::RequestLiquidate));
}

public void linkActive()
{
    QueryBuildDataSource    qbds = BankLGDocumentView_ds.query().dataSourceTable(tablenum(BankLGDocumentView));

    SysQuery::findOrCreateRange(qbds, fieldNum(BankLGDocumentView, OriginNum)).value(SysQuery::value(element.getOriginDocument().DocumentId));
    SysQuery::findOrCreateRange(qbds, fieldNum(BankLGDocumentView, OriginType)).value(SysQuery::value(element.getOriginDocument().BankLGDocumentMap::getOriginDocumentType()));

    super();

    element.setTitleText();
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        bankLGType.checkExpiryDate();
        element.updateActionStatus();
        element.setTitleText();
    }

    return ret;
}

public void delete()
{
    BankLGAction.requestDelete();
    BankLGAction_DS.reread();
    BankLGAction_DS.refresh();
    BankLGAction_DS.active();
}

public int active()
{
    int     ret;
    boolean allowDelete;

    ret = super();

    if (ret)
    {
        allowDelete = BankLGAction.validateRequestDelete();
        this.allowDelete(allowDelete);
        buttonLineActionRemove.enabled(allowDelete);
    }

    return ret;
}
