/// <summary>
/// Enables/disables charge of Letter of Credit feature parameter control.
/// </summary>
public void enableBankLCParameters_SA()
{
    // <GSA>
    #ISOCountryRegionCodes

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoSA]))
    {
        bankParameters_DS.object(fieldNum(BankParameters, BankLCImportChargeEnable_SA)).allowEdit(bankParameters.BankLCImportEnable);
    }
    // </GSA>
}

public void enablePDCAccounts(boolean _enable = false)
{
    pdcGroup_IssueClearingPDC.allowEdit(_enable);
    pdcGroup_ReceiptClearingPDC.allowEdit(_enable);
    pdcGroup_GeneraljournalNamePDC.allowEdit(_enable);
    pdcGroup_VendPaymentJournalPDC.allowEdit(_enable);
    pdcGroup_WHTClearingPDC.allowEdit(_enable);
}

public void init()
{
    // <GEERU><GEEU>
    isCashModuleEnabled = RCashParameters::isCashModuleEnabled();
    // </GEERU></GEEU>
    isCFMModuleEnabled = CFMParameters::isModuleEnabled();

    this.numberSeqPreInit();

    super();

    BankParameters::find();

    pdcGroup_IssueClearingPDC.parmPostingType(LedgerPostingType::None);

    pdcGroup_ReceiptClearingPDC.parmPostingType(LedgerPostingType::None);

    pdcGroup_WHTClearingPDC.parmPostingType(LedgerPostingType::None);

    // <GEERU><GEEU>
    if (isCashModuleEnabled)
    {
        RCashParameters::find();
    }
    // </GEERU></GEEU>
    if (isCFMModuleEnabled)
    {
        CFMParameters::find();
        tabPageCFM.visible(CFMParameters::isCompanyCentralizedPaymentCompany());
    }

    this.adjustBankCurrencyRevalVisibility();

    this.numberSeqPostInit();

    if (RemoveLoadReconciliationWorksheetExtensibleFlight::instance().isEnabled())
    {
        BankParameters_LoadReconciliationWorksheetExtensible.visible(false);
    }

    this.setPettyCashVisibility();
    if (FeatureStateProvider::isFeatureEnabled(BankRevalExchRateTypeEnhancementFeature::instance()))
    {
        ExchRateType.visible(true);
        this.updateExchangeRateTypeEnabled();
        this.updateExchangeRateTypeMandatory();
    }

    Netting.visible(FeatureStateProvider::isFeatureEnabled(CustVendNettingFeature::instance()));
}

private void updateExchangeRateTypeEnabled()
{
    BankParameters_ds.object(fieldNum(BankParameters, AccountingCurrencyExchangeRateType)).allowEdit(BankParameters.ExchAdjRateSource == BankRevalExchRateSource::Specific);
    BankParameters_ds.object(fieldNum(BankParameters, ReportingCurrencyExchangeRateType)).allowEdit(BankParameters.ExchAdjRateSource == BankRevalExchRateSource::Specific);
}

private void updateExchangeRateTypeMandatory()
{
    BankParameters_ds.object(fieldNum(BankParameters, AccountingCurrencyExchangeRateType)).mandatory(BankParameters.ExchAdjRateSource == BankRevalExchRateSource::Specific);
    BankParameters_ds.object(fieldNum(BankParameters, ReportingCurrencyExchangeRateType)).mandatory(BankParameters.ExchAdjRateSource == BankRevalExchRateSource::Specific);
}

private void adjustBankCurrencyRevalVisibility()
{
    #ISOCountryRegionCodes

    // Do not display the company specific settings if the feature is enabled globally or the country region is excluded.
    BankCurrencyRevalGroup.visible(false);
}

void numberSeqPostInit()
{
    allowSameAs.visible(numberSeqApplicationModule.sameAsActive()
        // <GEERU><GEEU>
        || (isCashModuleEnabled && numberSeqApplicationModuleRCash.sameAsActive())
        // </GEERU></GEEU>
        ||(isCFMModuleEnabled && numberSeqApplicationModuleCFM.sameAsActive()));

    labelSameAs.visible(allowSameAs.visible());
}

void numberSeqPreInit()
{
    runExecuteDirect = false;

    numberSequenceModules = [NumberSeqModule::Bank];

    numberSeqApplicationModule = new NumberSeqModuleBank();

    // <GEERU><GEEU>
    if (isCashModuleEnabled)
    {
        numberSequenceModules += NumberSeqModule::RCash;

        numberSeqApplicationModuleRCash = new NumberSeqModuleRCash();
    }
    // </GEERU></GEEU>
    if (isCFMModuleEnabled)
    {
        numberSequenceModules += NumberSeqModule::Cashflow;
        numberSeqApplicationModuleCFM = new NumberSeqModuleCFM();
    }

    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

/// <summary>
/// Sets control access for bank reconciliation matching validation.
/// </summary>
public void setControllAccessForMatchingValidation()
{
    bankParameters_ds.object(fieldNum(BankParameters, AllowedReconciliationMatchingMaxDiffDays)).allowEdit(bankParameters.CheckReconciliationMatchingDiffDays);
}

public void updateBankLGControl()
{
    bankParameters_ds.object(fieldNum(BankParameters, BankLGJournalName)).enabled(bankParameters.BankLGEnable);
}

private void setPettyCashVisibility()
{
    #ISOCountryRegionCodes
    boolean isDefaultOnCountry = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ, #isoEE, #isoHU, #isoLT, #isoLV, #isoPL, #isoRU]);
    boolean featureIsOn = FeatureStateProvider::isFeatureEnabled(PettyCashFeature::instance());
    boolean pettyCashIsEnabled = BankParameters::getEnablePettyCash();
    TabCash.visible(isDefaultOnCountry || featureIsOn || pettyCashIsEnabled);
    CashGeneralTabPage.visible(!isDefaultOnCountry && featureIsOn);
    this.setPettyCashParameterVisibility(isDefaultOnCountry || pettyCashIsEnabled);
}

private void setPettyCashParameterVisibility(boolean _visibility)
{
    DefaultValuesTabPage.visible(_visibility);
    CashOrderReuseNumTabPage.visible(_visibility);
    OperationsAmmountLimitTabPage.visible(_visibility);
    ValidationTabPage.visible(_visibility);
    DimensionTabPage.visible(_visibility);
    ApprovalTabPage.visible(_visibility);
}

public int active()
{
    int ret = super();

    element.setControllAccessForMatchingValidation();

    element.updateBankLGControl();
    // <GSA>
    element.enableBankLCParameters_SA();
    // </GSA>
    pdcGroup_postTransPDC.allowEdit(bankParameters.EnablePDC);
    element.enablePDCAccounts(bankParameters.PostTransPDC);

    if (FeatureStateProvider::isFeatureEnabled(BankChequeSequenceValidationFeature::instance()))
    {
        CheckParameters_CheckNumberInterval.visible(BankParameters.CheckNumberValidation == NoYes::Yes);
    }

    return ret;
}

