[Form]
public class BankPaymentOrderJour_RU extends FormRun
{
    LedgerJournalACType     ledgerACType;
    QueryBuildRange         criteriaCurrency;
    CFMPaymentRequestTable  paymentRequest;

    public const int ConPos1 = 1;
    public const int ConPos2 = 2;


}

private container getData()
{
    container   con;

    con = conpoke(con, ConPos1, paymOrderStatus.selection());
    con = conpoke(con, ConPos2, currency.text());

    return con;
}

public void init()
{
    super();

    switch (bankPaymentOrderJour_RU_ds.getLedgerACType())
    {
        case LedgerJournalACType::Cust :
            namePayer_1.visible(true);
            bankAccountPayer_1.visible(true);
            break;
        case LedgerJournalACType::Vend :
            nameRecipient_1.visible(true);
            bankAccountRecipient_1.visible(true);
            break;
        default:
            throw error(strfmt("@SYS22996",funcname()));
    }

    currency.text(CompanyInfoHelper::standardCurrency());
    if (element.args().dataset() == tableNum(CFMPaymentRequestTable))
    {
        BankPaymentOrderJour_RU_ds.query().allowCrossCompany(true);
    }
}

void isData(boolean _flag)
{
    voucher.enabled(_flag);
    printing.enabled(_flag);
    essentialElements.visible(_flag);

    if (! _flag)
    {
        tab.tab(1);
    }
}

/// <summary>
/// Gets the payer name.
/// </summary>
/// <returns>A string containing the name of the payer.</returns>
display CustName namePayer()
{
    BankPaymentFill_RU    bankPaymentFill;

    if (! bankPaymentOrderJour_RU)
    {
        return '';
    }

    switch (bankPaymentOrderJour_RU.LedgerACType)
    {
        case LedgerJournalACType::Cust :
            bankPaymentFill = BankPaymentFill_Cust_RU::construct(bankPaymentOrderJour_RU);
            break;
        case LedgerJournalACType::Vend :
            bankPaymentFill = BankPaymentFill_Vend_RU::construct(bankPaymentOrderJour_RU);
            break;
    }

    nameRecipient.text(bankPaymentFill.nameRecipient());

    bankAccountPayer.text(bankPaymentFill.bankAccountPayer());
    bankAccountRecipient.text(bankPaymentFill.bankAccountRecipient());

    addressPayer.text(bankPaymentFill.addressPayer());
    addressRecipient.text(bankPaymentFill.addressRecipient());

    inn_Payer.text(bankPaymentFill.inn_Payer());
    inn_Recipient.text(bankPaymentFill.inn_Recipient());

    bankNamePayer.text(bankPaymentFill.bankNamePayer());
    bankNameRecipient.text(bankPaymentFill.bankNameRecipient());

    bic_Payer.text(bankPaymentFill.bic_Payer());
    bic_Recipient.text(bankPaymentFill.bic_Recipient());

    corrBankAccountPayer.text(bankPaymentFill.corrBankAccountPayer());
    corrBankAccountRecipient.text(bankPaymentFill.corrBankAccountRecipient());

    addressBankPayer.text(bankPaymentFill.addressBankPayer());
    addressBankRecipient.text(bankPaymentFill.addressBankRecipient());

    return bankPaymentFill.namePayer();
}

void setButtonAccess()
{
    BankAccountTrans    bankAccountTrans;

    select firstonly RecId from bankAccountTrans
        where bankAccountTrans.PaymReference == bankPaymentOrderJour_RU.PaymentOrderNum &&
              bankAccountTrans.Voucher       == bankPaymentOrderJour_RU.Voucher;

    if (bankAccountTrans.RecId)
    {
        voucher.enabled(true);
    }
    else
    {
        voucher.enabled(false);
    }
}

public void init()
{
    if (element.args().dataset() == tableNum(CFMPaymentRequestTable))
    {
        paymentRequest = element.args().record();

        switch(paymentRequest.AccountType)
        {
            case CFMAccountType::Cust:
                ledgerACType = LedgerJournalACType::Cust;
                break;

            case CFMAccountType::Vend:
                ledgerACType = LedgerJournalACType::Vend;
                break;

            default:
                throw error(Error::wrongUseOfFunction(funcName()));

        }
    }
    else
    {
        ledgerACType = element.args().parmEnum();
    }

    super();

    if (bankPaymentOrderJour_RU_ds.getLedgerACType() == LedgerJournalACType::Cust)
    {
        this.cacheAddMethod(tablemethodstr(BankPaymentOrderJour_RU, namePayer));
        this.cacheAddMethod(tablemethodstr(BankPaymentOrderJour_RU, bankAccountPayer));
    }
    else
    {
        this.cacheAddMethod(tablemethodstr(BankPaymentOrderJour_RU, nameRecipient));
        this.cacheAddMethod(tablemethodstr(BankPaymentOrderJour_RU, bankAccountRecipient));
    }

    this.setJoinMode(PaymOrderStatus_RU::All);
}

public void executeQuery()
{
    if (currency.text())
    {
        criteriaCurrency.value(queryValue(currency.text()));
    }
    else
    {
        criteriaCurrency.value('');
    }

    super();

    if (! bankPaymentOrderJour_RU)
    {
        element.isData(false);
    }
    else
    {
        element.isData(true);
    }
}

public int active()
{
    int ret;

    ret = super();

    element.setButtonAccess();

    return ret;
}

public void setJoinMode(PaymOrderStatus_RU _orderStatus, BankClientPaymentType_RU _qElectronicPayment = BankClientPaymentType_RU::All)
{
    QueryBuildDataSource    qbdsMain;
    QueryBuildDataSource    qbdsJoined;
    QueryBuildRange         qbdsRange;
    Query                   query;

    void joinDS(str _value)
    {
        qbdsJoined = qbdsMain.addDataSource(tablenum(LedgerJournalTrans));
        qbdsJoined.addRange(fieldnum(LedgerJournalTrans, PaymentStatus)).value(_value);
        qbdsJoined.relations(true);
        qbdsJoined.joinMode(JoinMode::ExistsJoin);

        if (paymentRequest)
        {
            qbdsRange = SysQuery::findOrCreateRange(qbdsJoined, fieldNum(LedgerJournalTrans, RecId));
            qbdsRange.value(queryValue(CFMPaymentRequestPaymentLink::findPaymentSourceByPaymentRequest(paymentRequest.RecId, tableNum(LedgerJournalTrans)).RefRecId));
            qbdsRange.status(RangeStatus::Hidden);
        }

        qbdsJoined = qbdsJoined.addDataSource(tablenum(LedgerJournalTable));

        if (!paymentRequest)
        {
            qbdsJoined.addRange(fieldNum(LedgerJournalTable, Posted)).value(queryValue(NoYes::No));
        }

        qbdsJoined.relations(true);
        qbdsJoined.joinMode(JoinMode::ExistsJoin);
    }

    query       = new Query();
    qbdsMain    = query.addDataSource(tablenum(BankPaymentOrderJour_RU));
    qbdsMain.addRange(fieldnum(BankPaymentOrderJour_RU, LedgerACType)).value(enum2Value(ledgerACType));
    if (_qElectronicPayment != BankClientPaymentType_RU::All)
    {
        qbdsMain.addRange(fieldnum(BankPaymentOrderJour_RU, ElectronicPayment)).value(
                enum2Value(_qElectronicPayment == BankClientPaymentType_RU::Electronic ?
                            NoYes::Yes : NoYes::No));
    }
    criteriaCurrency = qbdsMain.addRange(fieldnum(BankPaymentOrderJour_RU, CurrencyCode));

    switch (_orderStatus)
    {
            case PaymOrderStatus_RU::All :
                if (paymentRequest)
                {
                    joinDS(SysQuery::valueUnlimited());
                }
                break;
            case PaymOrderStatus_RU::Created :
                joinDS(queryRangeConcat(queryValue(CustVendPaymStatus::Sent),CustVendPaymStatus::Recieved));
                break;
            case PaymOrderStatus_RU::Rejected :
                joinDS(queryValue(CustVendPaymStatus::Rejected));
                break;
            case PaymOrderStatus_RU::Approved :
                joinDS(queryValue(CustVendPaymStatus::Confirmed));
                break;
            case PaymOrderStatus_RU::Posted :
                qbdsJoined  = qbdsMain.addDataSource(tablenum(BankAccountTrans));
                qbdsJoined.relations(true);
                qbdsJoined.joinMode(JoinMode::ExistsJoin);
                break;
    }

    this.query(query);
}

public void jumpRef()
{
    Args            args;
    MenuFunction    menuFunction;

    if (bankPaymentOrderJour_RU.LedgerACType == LedgerJournalACType::Vend)
    {
        menuFunction = new MenuFunction(menuitemdisplaystr(LedgerJournalTable5), MenuItemType::Display);

        args         = new Args(element);

        // lookupField is used instead of args.record
        // to the same behaviour as super() on Cust
        args.lookupField(fieldnum(LedgerJournalTable, JournalNum));
        args.lookupValue(bankPaymentOrderJour_RU.JournalNum);

        if (menuFunction && args)
        {
            menuFunction.run(args);
        }
    }
    else
    {
        super();
    }
}

/// <summary>
/// This method is used to print ssrs <c>BankPaymentOrderJourReport_RU</c> report.
/// </summary>
public void clicked()
{
    Args         args;
    MenuFunction menuFunction;

    BankPaymentOrderJourReportController_RU controller  = new BankPaymentOrderJourReportController_RU();

    controller.setParametersValue(bankPaymentOrderJour_RU_ds.getLedgerACType(), paymOrderStatus.selection(), currency.text());

    args = new Args();
    args.parmObject(controller);

    menuFunction = new MenuFunction(menuitemoutputstr(BankPaymentOrderJourReport_RU), MenuItemType::Output);
    menuFunction.run(args);
}

