public class BankReconciliationMatchRule extends FormRun
{
    private boolean isBankModernReconciliationFeatureEnabled = FeatureStateProvider::isFeatureEnabled(BankModernReconciliationFeature::instance());
    private boolean isSkipReversalFlagValidation = FeatureStateProvider::isFeatureEnabled(BankSkipReversalFlagValidationFeature::instance());
    BankReconciliationMatchRuleFormView formView;
}

public void init()
{
    formView = BankReconciliationMatchRuleFormView::construct();

    super();

    formView.formInitPost(this);

    boolean isDefaultGroupCondition = BankReconciliationMatchingRuleDefaultGroupConditionFlight::instance().isEnabled();
    GroupStmtRemoveButton.visible(!isDefaultGroupCondition);
    GroupTransRemoveButton.visible(!isDefaultGroupCondition);
    GroupStmtRemoveButtonV2.visible(isDefaultGroupCondition);
    GroupTransRemoveButtonV2.visible(isDefaultGroupCondition);
}

void updateViewEditMode()
{
    if (!this.inViewMode())
    {
        if (bankReconciliationMatchRule.IsActive)
        {
            element.design().viewEditMode(ViewEditMode::View);
        }
        else if (!bankReconciliationMatchRule.RuleId)
        {
            element.design().viewEditMode(ViewEditMode::Edit);
        }
    }
    else
    {
        if (bankReconciliationMatchRule.IsActive)
        {
            element.design().viewEditMode(ViewEditMode::View);
        }
        else
        {
            element.design().viewEditMode(ViewEditMode::Auto);
        }
    }
}

private void addGroupRuleLines(BankReconciliationMatchRuleLine _matchRuleLine)
{
    if (BankReconciliationMatchRule.MatchActionType == BankReconciliationMatchActionType::Match
        && BankReconciliationMatchRule.MatchingType != BankReconciliationStmtMatchMathingType::OneToOne)
    {
        if (BankReconciliationMatchRule.isGroupBankTransaction())
        {
            BankReconciliationGroupRuleLine::insertDocGroupRuleLineFromDocMatchRuleLine(BankReconciliationMatchRule, _matchRuleLine.FieldId);
            BankReconciliationDocumentGroupRuleLine_DS.research(true);
        }

        if (BankReconciliationMatchRule.isGroupBankStatement())
        {
            BankReconciliationGroupRuleLine::insertStmtGroupRuleLineFromDocMatchRuleLine(BankReconciliationMatchRule, _matchRuleLine.CompareFieldId);
            BankReconciliationStmtGroupRuleLine_DS.research(true);
        }
    }
}

public int active()
{
    int ret = super();

    if (!isBankModernReconciliationFeatureEnabled)
    {
        // This code will skip calling the BankReconciliationMatchRuleLineDocument_ds.LinkActive() method and will have some issue.
        BankReconciliationMatchRuleLineDocument_ds.executeQuery();
    }

    BankReconciliationDocumentGroupRuleLine_ds.executeQuery();
    BankReconciliationStmtGroupRuleLine_ds.executeQuery();
    element.updateViewEditMode();

    formView.activeMatchRulePost(this.cursor());

    return ret;
}

public void delete()
{
    super();

    if (BankRefineReconcliationGroupConditionFlight::instance().isEnabled())
    {
        BankReconciliationMatchRule matchRule = this.cursor();
        if (!matchRule)
        {
            formView.deleteMatchRulePost();
        }
    }
}

public int active()
{
    int ret = super();

    if (isSkipReversalFlagValidation)
    {
        formView.updateStatusOfEditStatementField(this.cursor());
    }

    return ret;
}

public edit FieldLabel editStmtFieldLabel(
    boolean _set,
    BankReconciliationGroupRuleLine _ruleLine,
    FieldLabel _value)
{
    TableId tableId = tableNum(BankReconciliationStatementOpenTmp);

    if (_set)
    {
        _ruleLine.FieldId = BankReconciliationRuleParser::getFieldIdFromFieldLabel(tableId, _value);
    }

    return fieldId2PName(tableId, _ruleLine.FieldId);
}

public edit FieldLabel editDocFieldLabel(
    boolean _set,
    BankReconciliationGroupRuleLine _ruleLine,
    FieldLabel _value)
{
    TableId tableId = tableNum(BankReconciliationDocumentOpenTmp);

    if (_set)
    {
        _ruleLine.FieldId = BankReconciliationRuleParser::getFieldIdFromFieldLabel(tableId, _value);
    }

    return fieldId2PName(tableId, _ruleLine.FieldId);
}

public int active()
{
    boolean ret = super();

    if (isBankModernReconciliationFeatureEnabled)
    {
        formView.updateStatusOfBankReconciliationMatchRulePostingSetupControl(bankReconciliationMatchRule_PostingInfo.LegalEntity);
    }

    return ret;
}

public void linkActive()
{
    if (isBankModernReconciliationFeatureEnabled)
    {
        formView.linkActivePostingInfoPre();
    }
    super();
}

public void initValue()
{
    if (isBankModernReconciliationFeatureEnabled)
    {
        BankReconciliationMatchRuleLine matchInvoiceRuleLine = BankReconciliationMatchRuleLine::findBySysGeneratedType(
            BankReconciliationMatchRule.RecId,
            BankReconMatchRuleLineSysGeneratedType::None,
            BankReconciliationMatchRuleLineType::MatchOpenClosedCustInvoice);

        if (matchInvoiceRuleLine)
        {
            bankReconciliationMatchRule_PostingInfo.AutoMatchCustAccount = NoYes::Yes;
        }
    }

    super();
}

public void write()
{
    if (isBankModernReconciliationFeatureEnabled
        && BankReconciliationMatchRule.MatchActionType == BankReconciliationMatchActionType::CustPaym)
    {
        BankReconciliationMatchRule_PostingInfo postingInfo;
        select firstonly RecId from postingInfo
            where postingInfo.BankReconciliationMatchRule == BankReconciliationMatchRule.RecId
                && postingInfo.CustAccount != '';

        if (postingInfo && Box::okCancel(strFmt("@Bank:ClearPostInfoCustAccWarning"), DialogButton::Cancel) == DialogButton::Cancel)
        {
            return;
        }

        BankReconciliationMatchRule_PostingInfo::enableAutoCustAccountMatching(BankReconciliationMatchRule.RecId);
        BankReconciliationMatchRule_PostingInfo_ds.research();
    }
    super();
}

public boolean modified()
{
    BankReconciliationMatchActionType origMatchActionType = bankReconciliationMatchRule.MatchActionType;
    boolean ret;
    if (BankReconciliationMatchActionChangeConfirmFlight::instance().isEnabled())
    {
        if (Box::okCancel("@Bank:BankReconciliationMatchRuleActionTypeChangeWarning", DialogButton::Cancel) == DialogButton::Ok)
        {
            ret = super();
            if (ret && bankReconciliationMatchRule.MatchActionType != origMatchActionType)
            {
                formView.modifyMatchActionType();
            }
        }
        else
        {
            ret = true;
        }
    }
    else
    {
        ret = super();
        if (ret)
        {
            formView.modifyMatchActionType();
        }
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        formView.updateDesignOfMatchingType();
        if (BankReconciliationMatchingRuleDefaultGroupConditionFlight::instance().isEnabled())
        {
            BankReconciliationMatchRule_ds.research(true);
        }
        else
        {
            BankReconciliationMatchRule matchRule = BankReconciliationMatchRule_ds.cursor();
            matchRule.cleanGroupRule();
            BankReconciliationMatchRule_ds.write();
        }
    }

    return ret;
}

public void clicked()
{
    super();
    str dialogMsg;

    // Gets the default dialog message depends on whether lines are marked on the grid.
    BankReconciliationGroupRuleLine stmtGroupLine = BankReconciliationStmtGroupRuleLine_ds.getFirst(true);
    if (stmtGroupLine)
    {
        dialogMsg = "@GLS93975";
    }
    else
    {
        stmtGroupLine = BankReconciliationStmtGroupRuleLine;
        if (stmtGroupLine)
        {
            dialogMsg = strFmt("@GLS93976", GroupBankStatement.caption());
        }
    }

    // Gets bank statement fields that exist corresponding matching rule line.
    Container stmtFieldsList;
    str stmtFieldsListName;
    while (stmtGroupLine)
    {
        BankReconciliationMatchRuleLine matchRuleLine = BankReconciliationMatchRuleLine::findByLineTypeComparedFieldId(
            BankReconciliationMatchRule.RecId,
            BankReconciliationMatchRuleLineType::Document,
            stmtGroupLine.FieldId);
        if (matchRuleLine)
        {
            stmtFieldsList += stmtGroupLine.FieldId;
            stmtFieldsListName += stmtFieldsListName == '' ? '' : ', ';
            stmtFieldsListName += fieldId2PName(tableNum(BankReconciliationStatementOpenTmp), stmtGroupLine.FieldId);
        }

        stmtGroupLine = BankReconciliationStmtGroupRuleLine_ds.getNext();
    }

    // Updates the dialog message if at least one matching rule line exists will be deleted.
    if (conLen(stmtFieldsList) > 0)
    {
        dialogMsg = strFmt("@Bank:MatchRuleLineWithStmtFieldWillBeDeletedMessage", stmtFieldsListName);
    }

    // Pops dialog, delete group condition lines and matching rule lines.
    if (dialogMsg != '' && Box::yesNo(dialogMsg, DialogButton::No) == DialogButton::Yes)
    {
        stmtGroupLine = BankReconciliationStmtGroupRuleLine_ds.getFirst(true) ? BankReconciliationStmtGroupRuleLine_ds.getFirst(true) : BankReconciliationStmtGroupRuleLine;
        while (stmtGroupLine)
        {
            stmtGroupLine.delete();
            stmtGroupLine = BankReconciliationStmtGroupRuleLine_ds.getNext();
        }

        for (int i = 1; i <= conLen(stmtFieldsList); i++)
        {
            BankReconciliationMatchRuleLine::deleteMatchRuleLineForStmtGroupRuleLine(BankReconciliationMatchRule, conPeek(stmtFieldsList, i));
        }

        BankReconciliationMatchRule_DS.research(true);
    }
}

public boolean modified()
