    boolean ret = super();

    if (ret)
    {
        if (BankRefineReconcliationGroupConditionFlight::instance().isEnabled())
        {
            BankReconciliationStmtGroupRuleLine_ds.write();
        }
    }

    return ret;
}

public void clicked()
{
    super();
    str dialogMsg;

    // Gets the default dialog message depends on whether lines are marked on the grid.
    BankReconciliationGroupRuleLine documentGroupLine = BankReconciliationDocumentGroupRuleLine_ds.getFirst(true);
    if (documentGroupLine)
    {
        dialogMsg = "@GLS93975";
    }
    else
    {
        documentGroupLine = BankReconciliationDocumentGroupRuleLine;
        if (documentGroupLine)
        {
            dialogMsg = strFmt("@GLS93976", GroupBankTransaction.caption());
        }
    }

    // Gets bank document fields that exist corresponding matching rule line.
    Container documentFieldsList;
    str documentFieldsListName;
    while (documentGroupLine)
    {
        BankReconciliationMatchRuleLine matchRuleLine = BankReconciliationMatchRuleLine::findByLineTypeFieldId(
            BankReconciliationMatchRule.RecId,
            BankReconciliationMatchRuleLineType::Document,
            documentGroupLine.FieldId);
        if (matchRuleLine)
        {
            documentFieldsList += documentGroupLine.FieldId;
            documentFieldsListName += documentFieldsListName == '' ? '' : ', ';
            documentFieldsListName += fieldId2PName(tableNum(BankReconciliationDocumentOpenTmp), documentGroupLine.FieldId);
        }

        documentGroupLine = BankReconciliationDocumentGroupRuleLine_ds.getNext();
    }

    // Updates the dialog message if at least one matching rule line exists will be deleted.
    if (conLen(documentFieldsList) > 0)
    {
        dialogMsg = strFmt("@Bank:MatchRuleLineWithFieldWillBeDeletedMessage", documentFieldsListName);
    }

    // Pops dialog, delete group condition lines and matching rule lines.
    if (dialogMsg != '' && Box::yesNo(dialogMsg, DialogButton::No) == DialogButton::Yes)
    {
        documentGroupLine = BankReconciliationDocumentGroupRuleLine_ds.getFirst(true) ? BankReconciliationDocumentGroupRuleLine_ds.getFirst(true) : BankReconciliationDocumentGroupRuleLine;
        while (documentGroupLine)
        {
            documentGroupLine.delete();
            documentGroupLine = BankReconciliationDocumentGroupRuleLine_ds.getNext();
        }

        for (int i = 1; i <= conLen(documentFieldsList); i++)
        {
            BankReconciliationMatchRuleLine::deleteMatchRuleForDocumentGroupRuleLine(BankReconciliationMatchRule, conPeek(documentFieldsList, i));
        }

        BankReconciliationMatchRule_DS.research(true);
    }
}

