Method.Source:
[Form]
public class BankRemittanceFileTransactions extends FormRun
{
    private RecordSortedList custCache;
    private RecordSortedList vendCache;
    private ModuleCustVend moduleCustVend;

}

public void init()
{
    super();

    LedgerJournalTrans_AccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_AccountNum.parmJournalName(ledgerJournalTrans.ledgerJournalTable().JournalName);

    if (element.args().dataset() == tablenum(BankRemittanceFilesCust))
    {
        moduleCustVend = ModuleCustVend::Cust;
        billVendTrans.visible(false);
        billCustTrans.visible(true);
        ledgerJournalTrans_AmountCurDebit.visible(true);
        custCache = new RecordSortedList(tablenum(CustTrans));
        custCache.sortOrder(fieldnum(CustTrans, BankRemittanceFileId),
                            fieldnum(CustTrans, BillOfExchangeID));
    }
    else
    {
        moduleCustVend = ModuleCustVend::Vend;
        billVendTrans.visible(true);
        billCustTrans.visible(false);
        ledgerJournalTrans_BankRemittanceType.visible(false);
        ledgerJournalTrans_AmountCurCredit.visible(false);
        vendCache = new RecordSortedList(tablenum(VendTrans));
        vendCache.sortOrder(fieldnum(VendTrans, BankRemittanceFileId),
                            fieldnum(VendTrans, PromissoryNoteID));
    }
}

public int active()
{
    int ret;

    ret = super();

    LedgerJournalTrans_AccountNum.parmJournalName(ledgerJournalTrans.ledgerJournalTable().JournalName);
    LedgerJournalTrans_AccountNum.parmCurrency(ledgerJournalTrans.CurrencyCode);
    LedgerJournalTrans_AccountNum.parmDataAreaId(ledgerJournalTrans.Company ? ledgerJournalTrans.Company : curExt());
    LedgerJournalTrans_AccountNum.parmControlDate(ledgerJournalTrans.TransDate);

    return ret;
}

private CustTrans custCache(CustTrans _custTrans)
{
    CustTrans custTransLocal = _custTrans.data();

    if (!custCache.find(custTransLocal))
    {
        custTransLocal.TransDate = CustVendTransData::construct(_custTrans).maxDueDate();
        custTransLocal.BillOfExchangeStatus = CustBillOfExchangeTrans::getStatus(_custTrans);
        custCache.ins(custTransLocal);
    }

    return custTransLocal;
}

private VendTrans vendCache(VendTrans _vendTrans)
{
    VendTrans vendTransLocal = _vendTrans.data();

    if (!vendCache.find(vendTransLocal))
    {
        vendTransLocal.TransDate = CustVendTransData::construct(_vendTrans).maxDueDate();
        vendTransLocal.PromissoryNoteStatus = VendPromissoryNoteTrans::getStatus(_vendTrans);
        vendCache.ins(vendTransLocal);
    }

    return vendTransLocal;
}

public void jumpRef()
{
    str menuItemStr;

    switch (moduleCustVend)
    {
        case ModuleCustVend::Cust:
            menuItemStr = menuitemDisplayStr(BankRemittanceFileCust);
            break;

        case ModuleCustVend::Vend:
            menuItemStr = menuitemDisplayStr(BankRemittanceFileVend);
            break;

        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    MenuFunction menuFunction = new MenuFunction(menuItemStr, MenuItemType::Display);

    Args args = new Args();
    args.caller(element);

    FormRun formRun = menuFunction.create(args);
    formRun.run();
    formRun.wait();

    menuFunction.run(args);
}
