public display Voucher dispVoucher(BankStmtISOReportEntry _statementLine)
{
    if (!_statementLine.isPosted() && _statementLine.IsNewBankDocument)
    {
        BankStmtISOAccountStatement statement = formView.parmDSStatement().cursor();
        return statement.Voucher;
    }
    return _statementLine.Voucher;
}

