public void clicked()
{
    super();
    BankStmtISOAccountStatement statement = BankStmtISOAccountStatement::find(bankStmtISOAccountStatement.RecId);
    if (BankStatementPost::validateBankStatementBeforePost(statement.RecId))
    {
        BankStatementPost::postBankStatement(bankStmtISOAccountStatement.RecId);
        BankStmtISOAccountStatement_ds.research(true);
    }
}

public void clicked()
{
    if (!BankStmtISOReportEntry::existsNewForStatement(bankStmtISOAccountStatement.RecId))
    {
        BankStatementReversePost::reverseBankStatement(bankStmtISOAccountStatement);
        BankStmtISOAccountStatement_ds.research(true);
    }
    else
    {
        super();
    }
}

void clicked()
{
    if (bankStmtISOAccountStatement.Posted)
    {
        Args args = new Args();
        args.caller(element);
        args.record(bankStmtISOAccountStatement);

        FormRun formRun = new MenuFunction(menuitemDisplayStr(TaxTrans), MenuItemType::Display).create(args);
        formRun.run();
    }
    else
    {
        if (!FeatureStateProvider::isFeatureEnabled(BankStatementPostPerLineFeature::instance())
            || BankStmtISOReportEntry::updateEmptyAccountingDate(bankStmtISOAccountStatement))
        {
            BankStatementTaxDocument bankStatementTaxDocument = BankStatementTaxDocument::construct(bankStmtISOAccountStatement);
            bankStatementTaxDocument.calcTax();
            Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkBankStatement), bankStatementTaxDocument.tax(), bankStmtISOAccountStatement);
        }
    }
}

public void clicked()
{
    if (!FeatureStateProvider::isFeatureEnabled(BankStatementPostPerLineFeature::instance())
        || BankStmtISOReportEntry::updateEmptyAccountingDate(bankStmtISOAccountStatement))
    {
        if (!bankStmtISOAccountStatement.Posted && !TaxUncommitted::existByDocumentLineId(bankStmtISOReportEntry.TableId, bankStmtISOReportEntry.RecId))
        {
            BankStatementTaxDocument bankStatementTaxDocument = BankStatementTaxDocument::construct(bankStmtISOAccountStatement);
            bankStatementTaxDocument.calcTax();
        }

        super();
    }
}

void clicked()
{
    if (!FeatureStateProvider::isFeatureEnabled(BankStatementPostPerLineFeature::instance())
        || BankStmtISOReportEntry::updateEmptyAccountingDate(bankStmtISOAccountStatement))
    {
        BankStatementTaxDocument bankStatementTaxDocument = BankStatementTaxDocument::construct(bankStmtISOAccountStatement);
        bankStatementTaxDocument.calcTax(TaxUncommitted::existByDocumentLineId(bankStmtISOReportEntry.TableId, bankStmtISOReportEntry.RecId));
        bankStatementTaxDocument.tax().sourceSingleLine(true, true);
        Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkBankStatement), bankStatementTaxDocument.tax(), bankStmtISOReportEntry);
    }
}

