Method.Source:
[Form]
public class BankTrans extends FormRun
{
    Query query;
    QueryRun queryRun;

}

void init()
{
    // <GEERU>
    LedgerJournalTrans ledgerJournalTrans;
    // </GEERU>
    // <GEELV>
    #ISOCountryRegionCodes
    // </GEELV>

    super();

    query = new Query();

    query.addDataSource(tablenum(BankAccountTrans));
    // <GEERU>
    if (element.args().dataset() == tablenum(CashDueAnnouncementJournal_RU))
    {
        select firstonly Voucher from ledgerJournalTrans
            where ledgerJournalTrans.JournalNum == element.args().record().(fieldnum(CashDueAnnouncementJournal_RU, JournalNum))
                && ledgerJournalTrans.AnnouncementNum_RU == element.args().record().(fieldnum(CashDueAnnouncementJournal_RU, AnnouncementNum));

        query.dataSourceNo(1).addRange(fieldnum(BankAccountTrans, Voucher)).value(queryValue(ledgerJournalTrans.Voucher));
    }
    else
    {
        // </GEERU>
        query.dataSourceNo(1).addRange(fieldnum(BankAccountTrans, AccountId));
        query.dataSourceNo(1).addRange(fieldnum(BankAccountTrans, TransDate));
        element.promptVoucher();
        // <GEERU>
    }
    // </GEERU>

    // <GEELV>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoLV ]))
    {
        this.initDesign_LV();
    }
    // </GEELV>
}

void initDesign_LV()
{
    TabNotificationToCentralBank_LV.visible(true);
    bankAccountTrans_DS.allowEdit(true);

    //Disable editability of all fields for datasource
    allowEditFieldsOnFormDS_W(bankAccountTrans_DS, false);

    //Enable editability of Notification fields.
    bankAccountTrans_ds.object(fieldnum(BankAccountTrans, CorrespondentCountry_LV)).allowEdit(true);
    bankAccountTrans_ds.object(fieldnum(BankAccountTrans, CentralBankPurposeText_LV)).allowEdit(true);
    bankAccountTrans_ds.object(fieldnum(BankAccountTrans, CentralBankPurposeCode_LV)).allowEdit(true);
    bankAccountTrans_ds.object(fieldnum(BankAccountTrans, BankPaymentRegistrationNum_LV)).allowEdit(true);
}

void promptVoucher()
{
    queryRun = new QueryRun(query);

    if (!queryRun.prompt())
    {
        element.close();
    }

    // Add the join between BankAccountTrans and ReasonTableRef to the query.
    queryRun.query().dataSourceTable(tablenum(BankAccountTrans)).addDataSource(tablenum(ReasonTableRef));
    queryRun.query().dataSourceTable(tablenum(ReasonTableRef)).addLink(fieldnum(BankAccountTrans, ReasonRefRecID), fieldnum(ReasonTableRef, RecId));
    queryRun.query().dataSourceTable(tablenum(ReasonTableRef)).joinMode(JoinMode::OuterJoin);

    queryRun.query().dataSourceTable(tablenum(BankAccountTrans)).addSortField(fieldNum(BankAccountTrans, AccountId));
    queryRun.query().dataSourceTable(tablenum(BankAccountTrans)).addSortField(fieldNum(BankAccountTrans, TransDate), SortOrder::Descending);

    bankAccountTrans_ds.query(queryRun.query());
}

public void linkActive()
{
    LedgerJournalTrans ledgerJournalTrans;

    if (element.args().dataset() == tablenum(CashDueAnnouncementJournal_RU))
    {
        select firstonly Voucher from ledgerJournalTrans
            where ledgerJournalTrans.JournalNum == element.args().record().(fieldnum(CashDueAnnouncementJournal_RU, JournalNum))
                && ledgerJournalTrans.AnnouncementNum_RU == element.args().record().(fieldnum(CashDueAnnouncementJournal_RU, AnnouncementNum));

        query = this.query();
        findOrCreateRange_W(this.query().dataSourceTable(tablenum(BankAccountTrans)), fieldnum(BankAccountTrans, Voucher), ledgerJournalTrans.Voucher);
    }

    this.query(query);

    super();
}

public int active()
{
    int ret;

    ret = super();
    // <GEERU>

    cashDueAnnouncement_RU.enabled((select firstonly RecId from ledgerJournalTrans
                                        where ledgerJournalTrans.Voucher == bankAccountTrans.Voucher
                                            && ledgerJournalTrans.AnnouncementNum_RU).RecId);
    // </GEERU>

    if (ret)
    {
        originalDocument.enabled(false);
        cheques.enabled(false);
        deposit.enabled(false);
        voucherTransactions.enabled(true);

        if (bankAccountTrans.DepositNum)
        {
            voucherTransactions.enabled(false);
            deposit.enabled(true);
            originalDocument.enabled(true);
        }
        if (bankAccountTrans.ChequeNum)
        {
            cheques.enabled(true);
            originalDocument.enabled(true);
        }
    }
    return ret;
}

public boolean validateWrite()
{
    // <GEELV>
    // We do not want to execute the ReasonReferenceTable validateWrite to ensure the reason and reason comment are not blank.
    // BankAccountTrans table are posted transactions, but LV needs to be able edit the correspondent country information to ensure
    // legal country reporting.  The BankAccountTrans form does not allow editing of the ReasonReferenceTable fields, so we are
    // overriding the validateWrite functionality.
    boolean ret = true;

    return ret;
    // </GEELV>
}
