[Form]
public class BankVendPaymProposalSpec extends FormRun
{
    AmountCur payment[ ,1];
    SpecTrans specTransLocal;
    recId modified[ ,1];
    LedgerJournalEngine ledgerJournalEngine;
}

public void close()
{
    Object callerForm = element.args().caller();
    LedgerJournalTable  ledgerJournalTableLocal = element.args().caller().ledgerJournalTable();

    super();

    ttsbegin;
    LedgerJournalTrans::deleteIfZeroAmount(ledgerJournalTableLocal.JournalNum);
    ttscommit;

    if (callerForm && !this.closedCancel())
    {
        callerForm.refreshProposal();
    }
}

public void init()
{
    if (element.args().caller() && SysFormRun::hasMethod(element.args().caller(), identifierStr(ledgerJournalEngine)))
    {
        ledgerJournalEngine = element.args().caller().ledgerJournalEngine();
    }
    else
    {
        throw error(strFmt("@SYS76153",identifierStr(ledgerJournalEngine)));
    }

    super();

    GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceNO00008, funcName());
}

void update(SpecTrans   _specTrans)
{
    if (abs(_specTrans.Balance01) > abs(vendTransOpen.remainAmountCashDisc(ledgerJournalTrans.TransDate, VendPaymModeTable::find(ledgerJournalTrans.PaymMode).DiscGraceDays)))
    {
        _specTrans.Balance01     = vendTransOpen.remainAmountCashDisc(ledgerJournalTrans.TransDate, VendPaymModeTable::find(ledgerJournalTrans.PaymMode).DiscGraceDays);
        payment[specTrans.RecId] = CurrencyExchangeHelper::curAmount2CurAmount(vendTransOpen.remainAmountCashDisc(ledgerJournalTrans.TransDate, VendPaymModeTable::find(ledgerJournalTrans.PaymMode).DiscGraceDays), vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true);
    }

    if (_specTrans.Balance01 == 0)
    {
        _specTrans.Balance01 =  vendTransOpen.remainAmountCashDisc(ledgerJournalTrans.TransDate, VendPaymModeTable::find(ledgerJournalTrans.PaymMode).DiscGraceDays);
    }
}

public int active()
{
    int ret;

    ledgerJournalEngine.active(ledgerJournalTrans);

    if (ledgerJournalTrans.PaymentStatus == CustVendPaymStatus::None ||
        ledgerJournalTrans.PaymentStatus == CustVendPaymStatus::Rejected)
    {
        ledgerJournalTrans_ds.allowEdit(true);
        specTrans_ds.allowEdit(true);
        vendTrans_ds.allowEdit(true);
        vendTransOpen_ds.allowEdit(true);
        vendTransCashDisc_ds.allowEdit(true);

        ledgerJournalTrans_ds.allowDelete(true);
        specTrans_ds.allowDelete(true);
    }
    else
    {
        ledgerJournalTrans_ds.allowEdit(false);
        specTrans_ds.allowEdit(false);
        vendTrans_ds.allowEdit(false);
        vendTransOpen_ds.allowEdit(false);
        vendTransCashDisc_ds.allowEdit(false);

        ledgerJournalTrans_ds.allowDelete(false);
        specTrans_ds.allowDelete(false);
    }

    ret = super();

    if (ledgerJournalTrans_ds.cursor())
    {
        if (modified[ledgerJournalTrans.RecId] == ledgerJournalTrans.RecId)
        {
            this.reread();
        }
    }

    return ret;
}

public void write()
{
    ttsbegin;
    if (vendTransOpen.remainAmountCashDisc(ledgerJournalTrans.TransDate, VendPaymModeTable::find(ledgerJournalTrans.PaymMode).DiscGraceDays) < 0)
    {
        if ( specTrans.Balance01 > 0)
        {
            specTrans.Balance01 = -specTrans.Balance01;
        }
    }
    else
    {
        if (specTrans.Balance01 < 0)
        {
            specTrans.Balance01 = -specTrans.Balance01;
        }
    }

    if (ledgerJournalTrans.AmountCurCredit)
    {
        ledgerJournalTrans.AmountCurCredit -= (CurrencyExchangeHelper::curAmount2CurAmount(specTransLocal.Balance01, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true))
                                            - (CurrencyExchangeHelper::curAmount2CurAmount(specTrans.Balance01, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true));
    }
    if (ledgerJournalTrans.AmountCurDebit)
    {
        ledgerjournalTrans.AmountCurDebit  += (CurrencyExchangeHelper::curAmount2CurAmount(specTransLocal.Balance01, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true))
                                            - (CurrencyExchangeHelper::curAmount2CurAmount(specTrans.Balance01, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true));
    }
    modified[ledgerJournalTrans.RecId] = ledgerJournalTrans.RecId;

    ledgerJournalEngine.preWrite(ledgerjournaltrans);

    super();

    ledgerJournalEngine.write(ledgerjournaltrans);
    ttscommit;
}

public void delete()
{
    LedgerJournalTrans  findLedgerJournalTrans;
    recId               findRecId;

    if (!ledgerJournalTrans_ds.anyMarked())
    {
        findLedgerJournalTrans = this.getFirst();

        while (findLedgerJournalTrans)
        {
            if (findLedgerJournalTrans.RecId == ledgerJournalTrans.RecId)
            {
                findLedgerJournalTrans = this.getNext();
                findRecId = findLedgerJournalTrans.RecId ? findLedgerJournalTrans.RecId : findRecId ;
                break;
            }
            findRecId = findLedgerJournalTrans.RecId;
            findLedgerJournalTrans = this.getNext();
        }
    }

    ttsbegin;
    if (ledgerJournalTrans.AmountCurCredit)
    {
        ledgerJournalTrans.AmountCurCredit += (CurrencyExchangeHelper::curAmount2CurAmount(0, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true))
                                            - (CurrencyExchangeHelper::curAmount2CurAmount(specTrans.orig().Balance01, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true));
        if (ledgerJournalTrans.AmountCurCredit < 0)
        {
            ledgerjournalTrans.AmountCurDebit = abs(ledgerJournalTrans.AmountCurCredit);
            ledgerJournalTrans.AmountCurCredit = 0;
        }
    }
    else
    {
        ledgerjournalTrans.AmountCurDebit  -= (CurrencyExchangeHelper::curAmount2CurAmount(0, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true))
                                            - (CurrencyExchangeHelper::curAmount2CurAmount(specTrans.orig().Balance01, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true));
        if (ledgerjournalTrans.AmountCurDebit < 0)
        {
            ledgerJournalTrans.AmountCurCredit = abs(ledgerjournalTrans.AmountCurDebit);
            ledgerjournalTrans.AmountCurDebit  = 0;
        }
    }
    specTrans_ds.delete();
    ttscommit;

    ledgerJournalTrans.clear();
    ledgerJournalTrans_ds.research();

    findLedgerJournalTrans = LedgerJournalTrans::findRecId(findRecId, false);

    if (findLedgerJournalTrans)
    {
        ledgerJournalTrans_ds.research(false);
        element.args().lookupRecord(findLedgerJournalTrans);
    }
}

public void init()
{
    super();

    if (element.args().dataset() == tableNum(LedgerJournalTrans))
    {
        LedgerJournalTrans parmLedgerJournalTrans = element.args().record();
        this.query().dataSourceName('LedgerJournalTrans').clearDynalinks();
        this.query().dataSourceName('LedgerJournalTrans').addRange(fieldNum(LedgerJournalTrans, RecId)).value(strFmt('%1',parmLedgerJournalTrans.RecId));
    }
}

