{
    specTransLocal.Balance01 = specTrans.orig().Balance01;

    super();
}

edit AmountCur paymentAmount(boolean    set,
                             SpecTrans  _specTrans,
                             AmountCur  u)
{
    LedgerJournalTrans  ledgerJournalTransLocal;
    VendTrans           vendTransLocal;
    AmountCur           amountCurTmp;
    AmountCur           amountCurLocal = u;

    void fetchLedgerJournalTransLocal()
    {
        select firstonly LedgerDimension, CurrencyCode, TransDate
            from ledgerJournalTransLocal
            where ledgerJournalTransLocal.RecId   == _specTrans.SpecRecId &&
                  ledgerJournalTransLocal.TableId == _specTrans.SpecTableId;
    }

    if (_specTrans.RecId != 0)
    {
        vendTransLocal.data(vendTrans_ds.cursor());

        if (set)
        {
            fetchLedgerJournalTransLocal();
            _specTrans.Balance01 = CurrencyExchangeHelper::curAmount2CurAmount(u, ledgerJournalTransLocal.CurrencyCode, vendTransLocal.CurrencyCode, ledgerJournalTransLocal.TransDate, true);

            if (vendTransOpen.remainAmountCashDisc(ledgerJournalTrans.TransDate, VendPaymModeTable::discGraceDays(ledgerJournalTrans.PaymMode)) >= 0)
            {
                amountCurTmp = _specTrans.Balance01;
                if (amountCurTmp < 0)
                {
                    _specTrans.Balance01 = -amountCurTmp;
                }
                else
                {
                    _specTrans.Balance01 = amountCurTmp;
                }
            }
            else
            {
                amountCurTmp = _specTrans.Balance01;
                if (amountCurTmp < 0)
                {
                    _specTrans.Balance01 = amountCurTmp;
                }
                else
                {
                    _specTrans.Balance01 = -amountCurTmp;
                }
            }

            payment[_specTrans.RecId] = amountCurLocal;
            element.update(_specTrans);
        }
        else
        {
            if (payment[_specTrans.RecId])
            {
                amountCurLocal = payment[_specTrans.RecId];
            }
            else
            {
                fetchLedgerJournalTransLocal();
                payment[_specTrans.RecId] = CurrencyExchangeHelper::curAmount2CurAmount(_specTrans.Balance01, vendTransLocal.CurrencyCode, ledgerJournalTransLocal.CurrencyCode, ledgerJournalTransLocal.TransDate, true);
                amountCurLocal = payment[_specTrans.RecId];
            }
        }

        amountCurTmp = amountCurLocal;
        if (_specTrans.orig().Balance01 >= 0)
        {
            if (amountCurLocal < 0)
            {
                amountCurLocal = -amountCurTmp;
            }
            else
            {
                amountCurLocal = amountCurTmp;
            }
        }
        else
        {
            if (amountCurLocal < 0)
            {
                amountCurLocal = amountCurTmp;
            }
            else
            {
                amountCurLocal = -amountCurTmp;
            }
        }
        return amountCurLocal;
    }
    else
    {
        return 0;
    }
}

public void modified()
{
    SpecTrans   specTransTmp;

    element.update(specTrans);
    payment[specTrans.RecId] = 0;
    if (vendTransOpen.remainAmountCashDisc(ledgerJournalTrans.TransDate, VendPaymModeTable::find(ledgerJournalTrans.PaymMode).DiscGraceDays) >= 0)
    {
        specTransTmp.Balance01 = specTrans.Balance01;
        if (specTransTmp.Balance01 < 0)
        {
            specTrans.Balance01 = -specTransTmp.Balance01;
        }
        else
        {
            specTrans.Balance01 = specTransTmp.Balance01;
        }
    }
    else
    {
        specTransTmp.Balance01 = specTrans.Balance01;
        if (specTransTmp.Balance01 < 0)
        {
            specTrans.Balance01 = specTransTmp.Balance01;
        }
        else
        {
            specTrans.Balance01 = -specTransTmp.Balance01;
        }
    }
    super();
}

public void modified()
{
    super();
    if (vendTransOpen.CashDiscDate >= ledgerJournalTrans.TransDate)
    {
        ledgerJournalTrans.AmountCurDebit   -= CurrencyExchangeHelper::curAmount2CurAmount(vendTransOpen.orig().PossibleCashDisc, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true)
                                            - CurrencyExchangeHelper::curAmount2CurAmount(vendTransOpen.PossibleCashDisc, vendTrans.CurrencyCode, ledgerJournalTrans.CurrencyCode, ledgerJournalTrans.TransDate, true);
        specTrans.Balance01                 += vendTransOpen.orig().PossibleCashDisc - vendTransOpen.PossibleCashDisc;
    }
}

public void linkActive()
{
    super();
    vendTransCashDisc_ds.research(false);
    element.args().lookupRecord(VendTransCashDisc::findCashDisc(vendTransOpen.TableId,
                                                                    vendTransOpen.RecId,
                                                                    ledgerJournalTrans.TransDate,
                                                                    VendPaymModeTable::find(ledgerJournalTrans.PaymMode).DiscGraceDays));
}

