    public void init()
    {
        super();

        BrazilParameters::find();

        TaxationCodeSetup_BR::updateTaxTypeList();
        taxationCodeSetup_BR_ds.executeQuery();

        FiscalEstablishment_BR fiscalEstablishment;
        select firstonly RecId from fiscalEstablishment
            where fiscalEstablishment.EFDocNFeTechNotes == EFDocNFeTechNotes_BR::NFe402018005v110;
        if (!fiscalEstablishment)
        {
            TabNFeSetup.visible(false);
            brazilParameters_ds.object(fieldNum(BrazilParameters, TechnicalResponsibleCNPJ)).visible(false);
            brazilParameters_ds.object(fieldNum(BrazilParameters, TechnicalResponsibleContactName)).visible(false);
            brazilParameters_ds.object(fieldNum(BrazilParameters, TechnicalResponsibleCSRT)).visible(false);
            brazilParameters_ds.object(fieldNum(BrazilParameters, TechnicalResponsibleEmail)).visible(false);
            brazilParameters_ds.object(fieldNum(BrazilParameters, TechnicalResponsibleIdCSRT)).visible(false);
            brazilParameters_ds.object(fieldNum(BrazilParameters, TechnicalResponsiblePhone)).visible(false);
        }
		#ISOCountryRegionCodes
        TabRetail.visible(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]));

