public boolean validate()
{
    boolean ret = super();

    ret = brazilParameters.UseEMNFe == NoYes::Yes;

    if (ret)
    {
        if (Box::okCancel(
            "@NFe:Field_UseEMNFeHelp",
            DialogButton::Cancel,
            "@NFe:ActivateEMNFe") == DialogButton::Ok)
        {
            FiscalDocument_BR fiscaldocument;
            EFDocument_BR efdocument;
            Set documents = new Set(Types::String);

            while select FiscalDocumentNumber from fiscalDocument
            exists join efdocument
            where efdocument.RefRecId == fiscaldocument.RecId
                && (fiscalDocument.Status == FiscalDocumentStatus_BR::Created
                || efdocument.MessageStatus == EFDocMessageStatus_BR::Sent)
            {
                documents.add(fiscaldocument.FiscalDocumentNumber);
            }

            ret = documents.empty();

            if (!ret)
            {
                setPrefix("@NFe:NotAllDocumentsProcessed");

                Enumerator en = documents.getEnumerator();

                while (en.moveNext())
                {
                    error(strFmt("@NFe:FiscalDocNumber", en.current()));
                }
            }
        }
        else
        {
            ret = false;
        }
    }

    return ret;
}

public boolean validate()
{
    boolean ret = super();

    ret = brazilParameters.UseEMNFSe == NoYes::Yes;

    if (ret)
    {
        if (Box::okCancel(
            "@NFe:Field_UseEMNFSeHelp",
            DialogButton::Cancel,
            "@NFe:ActivateEMNFSe") != DialogButton::Ok)
        {
            ret = false;
        }
    }

    return ret;
}

