public boolean validate()
{
    DimensionAttribute  dimensionAttribute;

    dimensionAttribute  = DimensionAttribute::findByName(this.text());

    if (!dimensionAttribute || (dimensionAttribute.Type != DimensionAttributeType::CustomList && dimensionAttribute.Type != DimensionAttributeType::ExistingList))
    {
        return checkFailed(strFmt("@SYS94968", this.text(), "@SYS318800"));
    }

    return true;
}

public void lookup()
{
    BrazilParameters::lookupFEDimensionAttributes(this);
}

public boolean validate()
{
    DimensionAttribute  dimensionAttribute;

    dimensionAttribute  = DimensionAttribute::findByName(this.text());

    if (    !dimensionAttribute
        ||  (   (dimensionAttribute.BackingEntityTableName != tableStr(FiscalEstablishment_BR))
            &&  (dimensionAttribute.BackingEntityTableName != tableStr(DimensionFinancialTag))))
    {
        return checkFailed(strFmt("@SYS94968", this.text(), "@SYS318800"));
    }

    return true;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (BrazilParameters.ICMSOverrideICMSSTTaxationCode == false && this.value() == true)
    {
        if (Box::yesNo("@SYP4861207", DialogButton::No) == DialogButton::No)
        {
            ret = false;
        }
    }

    return ret;
