/// <summary>
/// Retrieves budgeting data from database by calling <c>BudgetAnalysisInquiryHelper_PSN</c> class.
/// </summary>
/// <param name="_treeControl">
/// The selected <c>FormTreeControl</c> to be initialized at child level.
/// </param>
/// <param name="_fromCache">
/// A <c>Boolean</c> value which mentions whether the budget analysis balances can be obtained from cache; optional.
/// </param>
public void getBudgetSummary(FormTreeControl _treeControl, boolean _fromCache = true)
{
    boolean         hasChildNodes;

    if ((!budgetAnalysisInquiryHelper.getIsInitializingTreeControl()) && (element.validateAllParameters()))
    {
        if ((!budgetAnalysisDimensionNode.parmIsLeafNode()) && (budgetAnalysisDimensionNode.parmChildMap().empty()))
        {
            hasChildNodes = budgetAnalysisInquiryHelper.insertTreeNodes(_treeControl, selectedNode.idx());

            if (!hasChildNodes)
            {
                budgetAnalysisDimensionNode.parmIsLeafNode(true);
            }
        }

        // call handler class to fill up grid
        budgetAnalysisInquiryHelper.parmBudgetAnalysisDimensionNode(budgetAnalysisDimensionNode);
        budgetAnalysisInquiryHelper.parmDoIncludeClosingEntries(ctrlIsClosingIncluded.value());
        budgetAnalysisInquiryHelper.getBudgetAnalysisSummary(budgetAnalysisSummaryTmpDetails, budgetAnalysisSummaryTmpSummary, _fromCache);

        element.updateDimensionColumnsLabel();

        budgetAnalysisSummaryTmpDetails_ds.executeQuery();
        budgetAnalysisSummaryTmpSummary_ds.executeQuery();
    }
}

/// <summary>
/// Hides or shows the parameter fields at the top of the form based on the current selection.
/// </summary>
public void hideShowFields()
{
    SysDictEnum enum = new SysDictEnum(enumNum(HideShowFields));

    ctrlShowHideButton.text(enum.index2Label(!ctrlShowHideComboBox.selection()));
    ctrlShowHideButton.imageLocation(SysImageLocation::EmbeddedResource);
    ctrlShowHideButton.normalImage(parametersHidden ? int2str(7886) : int2str(7882));

    parametersGroup.visible(ctrlShowHideComboBox.selection());
}

public void init()
{
    super();

    budgetAnalysisInquiryHelper = new BudgetAnalysisInquiryHelper_PSN();

    parametersHidden = false;
    ctrlShowHideComboBox.selection(HideShowFields::Show);
}

/// <summary>
/// Loads the parameter values that were saved when the user last closed the form.
/// </summary>
public void loadValues()
{
    container   packedForm;
    Version     version;

    packedForm = xSysLastValue::getValue(curext(), curUserId(), UtilElementType::Form, this.name());

    version = conPeek(packedForm, 1);

    if (version == #CurrentVersion)
    {
        [version, #CurrentList] = packedForm;

        ctrlShowHideComboBox.selection(hideShowFields);
        parametersHidden = ctrlShowHideComboBox.selection() == HideShowFields::Hide;

        ctrlPostingLayer.selection(enum2int(postingLayer));
        budgetAnalysisInquiryHelper.parmPostingLayer(postingLayer);

        ctrlColumnSet.selection(budgetAnalysisColumnSet);
        budgetAnalysisInquiryHelper.parmColumnSet(budgetAnalysisColumnSet);

        if ((strLen(dateCode) > 0) && (LedgerPeriodCode::exist(dateCode)))
        {
            ctrlDateInterval.text(dateCode);
        }

        if (fromDate != nullValue(fromDate))
        {
            ctrlFromDate.dateValue(fromDate);
            budgetAnalysisInquiryHelper.parmStartDate(fromDate);
        }

        if (toDate != nullValue(toDate))
        {
            ctrlToDate.dateValue(toDate);
            budgetAnalysisInquiryHelper.parmEndDate(toDate);
        }

        if (strLen(dimensionSetName) > 0)
        {
            dimensionHierarchy = DimensionHierarchy::findByTypeAndName(DimensionHierarchyType::Focus, dimensionSetName);

            if (dimensionHierarchy)
            {
                ctrlDimensionSet.text(dimensionSetName);
                budgetAnalysisInquiryHelper.parmDimensionHierarchyRecId(dimensionHierarchy.RecId);

                this.updateFilterControls();

                if (strLen(filterDimensionAttribute) > 0)
                {
                    ctrlFilterDimension.selection(ctrlFilterDimension.find(filterDimensionAttribute));
                    budgetAnalysisInquiryHelper.parmFilterDimensionAttribute(filterDimensionAttribute);
                }

                if (strLen(filterDimensionAttributeValue) > 0)
                {
                    ctrlFilterDimensionValue.text(filterDimensionAttributeValue);
                    budgetAnalysisInquiryHelper.parmFilterDimensionAttributeValue(filterDimensionAttributeValue);
                }

                budgetAnalysisInquiryHelper.initTreeControl(ctrlDimensionTree);
                budgetAnalysisInquiryHelper.initDimensionAttributeNames();
            }
        }
    }

    this.updateDrillDownButtons();
    this.hideShowFields();
}

