/// <summary>
/// Saves the currently selected parameter values for the user so that they will load the next time he opens the form.
/// </summary>
public void saveValues()
{
    budgetModelId                   = ctrlBudgetModel.valueStr();
    dateCode                        = ctrlDateInterval.valueStr();
    fromDate                        = ctrlFromDate.dateValue();
    toDate                          = ctrlToDate.dateValue();
    postingLayer                    = ctrlPostingLayer.selection();
    hideShowFields                  = ctrlShowHideComboBox.selection();
    budgetAnalysisColumnSet         = ctrlColumnSet.selection();
    dimensionSetName                = ctrlDimensionSet.valueStr();
    filterDimensionAttribute        = ctrlFilterDimension.valueStr();
    filterDimensionAttributeValue   = ctrlFilterDimensionValue.valueStr();

    xSysLastValue::putValue([#CurrentVersion, #CurrentList], curext(), curUserId(), UtilElementType::Form, this.name());
}

/// <summary>
/// Updates the grids' dimension column labels based on selected column set.
/// </summary>
public void updateDimensionColumnsLabel()
{
    if (budgetAnalysisInquiryHelper.parmColumnSet() == BudgetAnalysisColumnSet_PSN::ExpenseBudgetWithCarryForward)
    {
        ctrlDetailsGridCarryForward_DimensionValue.label(budgetAnalysisInquiryHelper.getDimensionAttributeNameDetail());
        ctrlSummaryGridCarryForward_DimensionValue.label(budgetAnalysisInquiryHelper.getDimensionAttributeNameSummary());
    }
    else
    {
        ctrlDetailsGrid_DimensionValue.label(budgetAnalysisInquiryHelper.getDimensionAttributeNameDetail());
        ctrlSummaryGrid_DimensionValue.label(budgetAnalysisInquiryHelper.getDimensionAttributeNameSummary());
    }
}

/// <summary>
/// Updates the enabled state of the drill down buttons.
/// </summary>
public void updateDrillDownButtons()
{
    BudgetAnalysisColumnSet_PSN columnSet = ctrlColumnSet.selection();

    revisedBudgetButton.enabled(
        columnSet == BudgetAnalysisColumnSet_PSN::ExpenseBudget
        || columnSet == BudgetAnalysisColumnSet_PSN::ExpenseBudgetWithCarryForward
        || columnSet == BudgetAnalysisColumnSet_PSN::RevenueBudget);

    actualsButton.enabled(
        columnSet == BudgetAnalysisColumnSet_PSN::ExpenseBudget
        || columnSet == BudgetAnalysisColumnSet_PSN::ExpenseBudgetWithCarryForward
        || columnSet == BudgetAnalysisColumnSet_PSN::RevenueBudget);

    encumbranceButton.enabled(
        columnSet == BudgetAnalysisColumnSet_PSN::ExpenseBudget
        || columnSet == BudgetAnalysisColumnSet_PSN::ExpenseBudgetWithCarryForward);

    preencumbranceButton.enabled(columnSet == BudgetAnalysisColumnSet_PSN::ExpenseBudget);
}

/// <summary>
/// Updates the filter controls.
/// </summary>
public void updateFilterControls()
{
    DimensionHierarchyLevel dimensionHierarchyLevel;
    DimensionAttribute      dimensionAttributeLocal;
    Name                    priorDimensionAttribute = budgetAnalysisInquiryHelper.parmFilterDimensionAttribute();
    boolean                 comboContainsPriorName = false;

    ctrlFilterDimension.clear();

    if (dimensionHierarchy)
    {
        while select Name, RecId
            from dimensionAttributeLocal
            exists join dimensionHierarchyLevel
                where dimensionHierarchyLevel.DimensionAttribute == dimensionAttributeLocal.RecId
                    && dimensionHierarchyLevel.DimensionHierarchy == dimensionHierarchy.RecId
        {
            ctrlFilterDimension.add(dimensionAttributeLocal.Name);

            if (dimensionAttributeLocal.Name == priorDimensionAttribute)
            {
                comboContainsPriorName = true;
            }
        }
    }

    if (comboContainsPriorName)
    {
        ctrlFilterDimension.selection(ctrlFilterDimension.find(priorDimensionAttribute));
        if ((strLen(ctrlFilterDimensionValue.valueStr()) > 0) && (!budgetAnalysisInquiryHelper.validateDimensionAttributeValueFilter(ctrlFilterDimensionValue.valueStr())))
        {
            ctrlFilterDimensionValue.text(nullValue(filterDimensionAttributeValue));
        }
    }
    else
    {
        ctrlFilterDimensionValue.text(nullValue(filterDimensionAttributeValue));
        budgetAnalysisInquiryHelper.parmFilterDimensionAttribute(ctrlFilterDimension.valueStr());
        budgetAnalysisInquiryHelper.parmFilterDimensionAttributeValue(ctrlFilterDimensionValue.valueStr());
    }
}

/// <summary>
/// Validates all of the parameters raising the appropriate info log messages if any violations are found.
/// </summary>
/// <returns>
/// true if all parameters are valid; otherwise false.
/// </returns>
public boolean validateAllParameters()
{
    boolean allParmsAreValid = true;

    allParmsAreValid = (allParmsAreValid && budgetAnalysisInquiryHelper.validateDimensionSet(ctrlDimensionSet.valueStr()));

    if (ctrlDateInterval.valueStr())
    {
        allParmsAreValid = allParmsAreValid && LedgerPeriodCode::checkExist(ctrlDateInterval.valueStr());
    }

    if (!ctrlFromDate.valueStr() || !ctrlToDate.valueStr())
    {
        allParmsAreValid = checkFailed("@GLS150261");
    }
    else
    {
        allParmsAreValid = (allParmsAreValid && budgetAnalysisInquiryHelper.validateDateRangeOrder(ctrlFromDate.dateValue(), ctrlToDate.dateValue()));
    }

    return allParmsAreValid;
}

void clicked()
{
    super();

    budgetAnalysisDimensionNode = selectedNode.data();

    if (ctrlDimensionTree.getSelection() && element.validateAllParameters())
    {
        element.getBudgetSummary(ctrlDimensionTree, false);
    }
}

public boolean modified()
{
    boolean isModified;

    isModified = super();

    if (isModified)
    {
        if (strLen(this.valueStr()) > 0)
        {
            dimensionHierarchy = DimensionHierarchy::findByTypeAndName(DimensionHierarchyType::Focus, this.valueStr());
            budgetAnalysisInquiryHelper.parmDimensionHierarchyRecId(dimensionHierarchy.RecId);
            element.updateFilterControls();
            budgetAnalysisInquiryHelper.initTreeControl(ctrlDimensionTree);
            // initialize financial dimension names for the selected dimension set
            budgetAnalysisInquiryHelper.initDimensionAttributeNames();
        }
        else
        {
            // The user cleared out the dimension set combo so clear the tree
            ctrlDimensionTree.deleteAll();
            dimensionHierarchy.clear();
            ctrlFilterDimension.clear();
            budgetAnalysisInquiryHelper.parmFilterDimensionAttribute(ctrlFilterDimension.valueStr());
            ctrlFilterDimensionValue.text(nullValue(filterDimensionAttributeValue));
            budgetAnalysisInquiryHelper.parmFilterDimensionAttributeValue(ctrlFilterDimensionValue.valueStr());
            budgetAnalysisInquiryHelper.parmDimensionHierarchyRecId(0);
        }

        element.refreshGrids(budgetAnalysisInquiryHelper.parmColumnSet());
    }

    return isModified;
}

public boolean validate()
{
    boolean isValid;

    isValid = super();

    isValid = (isValid && budgetAnalysisInquiryHelper.validateDimensionSet(this.valueStr()));

    return isValid;
}

public boolean modified()
{
    boolean                     isModified;

    isModified = super();

    if (isModified)
    {
        budgetAnalysisInquiryHelper.parmColumnSet(this.selection());
        budgetAnalysisInquiryHelper.initTreeControl(ctrlDimensionTree);
        element.updateDrillDownButtons();
        element.refreshGrids(budgetAnalysisInquiryHelper.parmColumnSet());
    }

    return isModified;
}

public int selectionChange()
{
    int selectionChanged;

    selectionChanged = super();

    if (selectionChanged)
    {
        budgetAnalysisInquiryHelper.parmPostingLayer(ctrlPostingLayer.selection());
    }

    return selectionChanged;
}

